"use client"

import type React from "react"
import React, { useState } from "react"
import { supabase } from "@/lib/supabase"
import { useAuth } from "@/contexts/AuthContext"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Textarea } from "@/components/ui/textarea"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Label } from "@/components/ui/label"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"

interface AddFoodProps {
  onFoodAdded: () => void
}

export default function AddFood({ onFoodAdded }: AddFoodProps) {
  const { user } = useAuth()
  const [loading, setLoading] = useState(false)
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
    quantity: "",
    expiry_date: "",
    pickup_location: "",
  })
  const [imageFile, setImageFile] = useState<File | null>(null)

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (!user) return

    setLoading(true)

    try {
      let imageUrl = null

      // Upload image if provided
      if (imageFile) {
        const fileExt = imageFile.name.split(".").pop()
        const fileName = `${user.id}/${Date.now()}.${fileExt}`

        const { error: uploadError } = await supabase.storage.from("food-images").upload(fileName, imageFile)

        if (uploadError) {
          console.error("Upload error:", uploadError)
          // Continue without image if upload fails
        } else {
          const {
            data: { publicUrl },
          } = supabase.storage.from("food-images").getPublicUrl(fileName)
          imageUrl = publicUrl
        }
      }

      // Insert food item
      const { error } = await supabase.from("food_items").insert({
        title: formData.title,
        description: formData.description || null,
        category: formData.category,
        quantity: formData.quantity,
        expiry_date: formData.expiry_date || null,
        pickup_location: formData.pickup_location,
        image_url: imageUrl,
        user_id: user.id,
      })

      if (error) {
        throw error
      }

      // Reset form
      setFormData({
        title: "",
        description: "",
        category: "",
        quantity: "",
        expiry_date: "",
        pickup_location: "",
      })
      setImageFile(null)

      onFoodAdded()
      alert("Food item shared successfully!")
    } catch (error: any) {
      console.error("Error adding food item:", error)
      alert(`Error sharing food item: ${error.message}`)
    } finally {
      setLoading(false)
    }
  }

  const categories = ["fruits", "vegetables", "grains", "dairy", "meat", "prepared", "other"]

  return (
    <Card className="max-w-2xl mx-auto">
      <CardHeader>
        <CardTitle className="text-2xl text-green-700">Share Food</CardTitle>
        <CardDescription>Help reduce waste by sharing food with your community</CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="title">Food Title *</Label>
            <Input
              id="title"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              placeholder="e.g., Fresh tomatoes, Cooked rice"
              required
            />
          </div>

          <div>
            <Label htmlFor="category">Category *</Label>
            <Select
              value={formData.category}
              onValueChange={(value) => setFormData({ ...formData, category: value })}
              required
            >
              <SelectTrigger>
                <SelectValue placeholder="Select category" />
              </SelectTrigger>
              <SelectContent>
                {categories.map((category) => (
                  <SelectItem key={category} value={category}>
                    {category.charAt(0).toUpperCase() + category.slice(1)}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label htmlFor="quantity">Quantity *</Label>
            <Input
              id="quantity"
              value={formData.quantity}
              onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
              placeholder="e.g., 2 kg, 5 pieces, 1 bowl"
              required
            />
          </div>

          <div>
            <Label htmlFor="pickup_location">Pickup Location *</Label>
            <Input
              id="pickup_location"
              value={formData.pickup_location}
              onChange={(e) => setFormData({ ...formData, pickup_location: e.target.value })}
              placeholder="e.g., Area 25, Lilongwe"
              required
            />
          </div>

          <div>
            <Label htmlFor="expiry_date">Expiry Date (optional)</Label>
            <Input
              id="expiry_date"
              type="date"
              value={formData.expiry_date}
              onChange={(e) => setFormData({ ...formData, expiry_date: e.target.value })}
            />
          </div>

          <div>
            <Label htmlFor="description">Description</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              placeholder="Additional details about the food..."
              rows={3}
            />
          </div>

          <div>
            <Label htmlFor="image">Photo (optional)</Label>
            <Input
              id="image"
              type="file"
              accept="image/*"
              onChange={(e) => setImageFile(e.target.files?.[0] || null)}
            />
          </div>

          <Button type="submit" className="w-full bg-green-600 hover:bg-green-700" disabled={loading}>
            {loading ? "Sharing..." : "Share Food"}
          </Button>
        </form>
      </CardContent>
    </Card>
  )
}

components/AddItem.tsx.content:
"use client";

import type React from "react"
import React, { useState, useEffect } from "react";
import { supabase } from "@/lib/supabase";
import { useAuth } from "@/contexts/AuthContext";
import { useLocation } from "@/contexts/LocationContext";
import { geocodeAddress, getCityCoordinates } from "@/lib/locationService";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import {
  Camera,
  Package,
  MapPin,
  Heart,
  ArrowLeft,
  ArrowRight,
  CheckCircle,
  Sparkles,
  Users,
  Globe,
} from "lucide-react";
import Image from "next/image";

interface AddItemProps {
  itemType: "food" | "non-food";
  onItemAdded: () => void;
}

const STEPS = [
  { id: 1, title: "Photo", icon: Camera, description: "Show your item" },
  {
    id: 2,
    title: "Details",
    icon: Package,
    description: "What are you sharing?",
  },
  { id: 3, title: "Location", icon: MapPin, description: "Where to collect" },
  { id: 4, title: "Impact", icon: Heart, description: "Choose destination" },
];

export default function AddItem({ itemType, onItemAdded }: AddItemProps) {
  const { user } = useAuth();
  const { selectedLocation } = useLocation();
  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [showSuccess, setShowSuccess] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
    quantity: "",
    condition: "",
    expiry_date: "",
    pickup_location: "",
    area: "",
    is_anonymous: false,
    collaboration_id: null as string | null,
  });
  const [collaborations, setCollaborations] = useState<any[]>([]);
  const [loadingCollaborations, setLoadingCollaborations] = useState(false);
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string | null>(null);

  // Fetch collaborations on component mount
  useEffect(() => {
    const fetchCollaborations = async () => {
      setLoadingCollaborations(true);
      try {
        const { data, error } = await supabase
          .from("collaboration_requests")
          .select("id, title, status")
          .eq("status", "active")
          .order("created_at", { ascending: false });

        if (error) {
          console.warn("Could not fetch collaborations:", error);
          setCollaborations([]);
        } else {
          setCollaborations(data || []);
        }
      } catch (error) {
        console.warn("Error fetching collaborations:", error);
        setCollaborations([]);
      } finally {
        setLoadingCollaborations(false);
      }
    };

    fetchCollaborations();
  }, []);

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onload = () => {
        setImagePreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const getCategoryIcon = (category: string) => {
    const icons: Record<string, string> = {
      // Food categories
      fruits: "üçé",
      vegetables: "ü•ï",
      grains: "üåæ",
      dairy: "ü•õ",
      meat: "üçñ",
      prepared: "üçΩÔ∏è",
      // Non-food categories
      clothing: "üëï",
      shoes: "üëü",
      household: "üè†",
      electronics: "üì±",
      books: "üìö",
      toys: "üß∏",
      "baby-items": "üçº",
      other: "üì¶",
    };
    return icons[category] || "üì¶";
  };

  const getCategories = () => {
    if (itemType === "food") {
      return [
        { value: "fruits", label: "Fresh Fruits", icon: "üçé" },
        { value: "vegetables", label: "Vegetables", icon: "ü•ï" },
        { value: "grains", label: "Grains & Rice", icon: "üåæ" },
        { value: "dairy", label: "Dairy Products", icon: "ü•õ" },
        { value: "meat", label: "Meat & Fish", icon: "üçñ" },
        { value: "prepared", label: "Prepared Food", icon: "üçΩÔ∏è" },
        { value: "other", label: "Other Food", icon: "ü•ó" },
      ];
    } else {
      return [
        { value: "clothing", label: "Clothing", icon: "üëï" },
        { value: "shoes", label: "Shoes", icon: "üëü" },
        { value: "household", label: "Household Items", icon: "üè†" },
        { value: "electronics", label: "Electronics", icon: "üì±" },
        { value: "books", label: "Books", icon: "üìö" },
        { value: "toys", label: "Toys & Games", icon: "üß∏" },
        { value: "baby-items", label: "Baby Items", icon: "üçº" },
        { value: "other", label: "Other Items", icon: "üì¶" },
      ];
    }
  };

  const conditions = [
    { value: "new", label: "Brand New", description: "Never used" },
    {
      value: "excellent",
      label: "Excellent",
      description: "Like new condition",
    },
    { value: "good", label: "Good", description: "Minor wear" },
    { value: "fair", label: "Fair", description: "Some wear but functional" },
    {
      value: "needs-repair",
      label: "Needs Repair",
      description: "Fixable issues",
    },
    { value: "old", label: "Old", description: "Well-used but working" },
  ];

  const areaOptions = [
    "area 18",
    "area 25",
    "area 23",
    "area 43",
    "area 30",
    "area 15",
    "area 14",
    "area 49",
    "Chilinde",
    "Kawale",
  ];

  const canProceedToNext = () => {
    switch (currentStep) {
      case 1:
        return true; // Image is optional but encouraged
      case 2:
        return (
          formData.title.trim() &&
          formData.category &&
          formData.quantity.trim() &&
          (itemType === "food" || formData.condition)
        );
      case 3:
        return formData.pickup_location.trim() && formData.area;
      case 4:
        return true; // Impact step has default values
      default:
        return false;
    }
  };

  const handleNext = () => {
    if (canProceedToNext() && currentStep < 4) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSubmit = async () => {
    setError(null);

    if (!user) {
      setError("Please sign in to share items");
      return;
    }

    // Final validation
    if (
      !formData.title.trim() ||
      !formData.category ||
      !formData.quantity.trim() ||
      !formData.pickup_location.trim() ||
      !formData.area
    ) {
      setError("Please fill in all required fields");
      return;
    }

    if (itemType === "non-food" && !formData.condition) {
      setError("Please select condition for non-food items");
      return;
    }

    setLoading(true);

    try {
      let imageUrl = null;

      // Upload image if provided
      if (imageFile) {
        const fileExt = imageFile.name.split(".").pop();
        const fileName = `${user.id}/${Date.now()}.${fileExt}`;

        try {
          const { error: uploadError } = await supabase.storage
            .from("item-images")
            .upload(fileName, imageFile);

          if (!uploadError) {
            const {
              data: { publicUrl },
            } = supabase.storage.from("item-images").getPublicUrl(fileName);
            imageUrl = publicUrl;
          }
        } catch (uploadErr) {
          console.warn("Image upload error:", uploadErr);
        }
      }

      // Get location coordinates
      let latitude = null;
      let longitude = null;

      if (selectedLocation) {
        latitude = selectedLocation.latitude;
        longitude = selectedLocation.longitude;
      } else if (formData.area) {
        // Try to get coordinates from the selected area/city
        const cityLocation = getCityCoordinates(formData.area);
        if (cityLocation) {
          latitude = cityLocation.latitude;
          longitude = cityLocation.longitude;
        }
      }

      // Prepare item data
      const itemData = {
        title: formData.title.trim(),
        description: formData.description.trim() || null,
        category: formData.category,
        item_type: itemType,
        quantity: formData.quantity.trim(),
        pickup_location: formData.pickup_location.trim(),
        latitude,
        longitude,
        user_id: user.id,
        status: "available",
        collaboration_id: formData.collaboration_id,
        image_url: imageUrl,
        expiry_date:
          itemType === "food" && formData.expiry_date
            ? formData.expiry_date
            : null,
        condition:
          itemType === "non-food" && formData.condition
            ? formData.condition
            : null,
        area: formData.area,
        is_anonymous: formData.is_anonymous,
      };

      const { data, error: insertError } = await supabase
        .from("items")
        .insert(itemData)
        .select();

      if (insertError) {
        throw insertError;
      }

      // Show success animation
      setShowSuccess(true);

      // Wait for animation then reset
      setTimeout(() => {
        setShowSuccess(false);
        setCurrentStep(1);
        setFormData({
          title: "",
          description: "",
          category: "",
          quantity: "",
          condition: "",
          expiry_date: "",
          pickup_location: "",
          area: "",
          is_anonymous: false,
          collaboration_id: null,
        });
        setImageFile(null);
        setImagePreview(null);
        onItemAdded();
      }, 3000);
    } catch (error: any) {
      console.error("Error adding item:", error);
      setError(`Failed to share item: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Success animation overlay
  if (showSuccess) {
    return (
      <div className="fixed inset-0 bg-green-50 flex items-center justify-center z-50">
        <div className="text-center space-y-6 animate-bounce">
          <div className="w-24 h-24 bg-green-500 rounded-full flex items-center justify-center mx-auto">
            <Sparkles className="w-12 h-12 text-white animate-pulse" />
          </div>
          <div className="space-y-2">
            <h2 className="text-3xl font-bold text-green-700">Amazing! üéâ</h2>
            <p className="text-lg text-green-600">
              You've helped someone in your neighborhood!
            </p>
            <p className="text-sm text-green-500">
              {itemType === "food"
                ? "Food rescued from waste"
                : "Item given new life"}{" "}
              ‚ú®
            </p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto bg-white min-h-screen">
      {/* Header with Progress */}
      <div className="sticky top-0 bg-white border-b border-gray-100 p-4 z-10">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-xl font-bold text-gray-900">
            Share {itemType === "food" ? "Food" : "Items"}
          </h1>
          <div className="text-sm text-gray-500">{currentStep}/4</div>
        </div>

        {/* Progress Bar */}
        <div className="flex space-x-2">
          {STEPS.map((step, index) => (
            <div
              key={step.id}
              className={`flex-1 h-2 rounded-full transition-all duration-300 ${
                currentStep > step.id
                  ? "bg-green-500"
                  : currentStep === step.id
                    ? "bg-green-300"
                    : "bg-gray-200"
              }`}
            />
          ))}
        </div>

        {/* Current Step Info */}
        <div className="mt-4 text-center">
          <div className="flex items-center justify-center space-x-2 mb-2">
            {React.createElement(STEPS[currentStep - 1].icon, {
              className: "w-5 h-5 text-green-600",
            })}
            <h2 className="text-lg font-semibold text-gray-900">
              {STEPS[currentStep - 1].title}
            </h2>
          </div>
          <p className="text-sm text-gray-600">
            {STEPS[currentStep - 1].description}
          </p>
        </div>
      </div>

      {/* Form Content */}
      <div className="p-4">
        {error && (
          <div className="mb-4 p-4 bg-red-50 border border-red-200 text-red-700 rounded-lg">
            <strong>Oops!</strong> {error}
          </div>
        )}

        {/* Step 1: Photo */}
        {currentStep === 1 && (
          <div className="space-y-6 py-6">
            <div className="text-center">
              <h3 className="text-lg font-medium mb-2">Add a photo</h3>
              <p className="text-gray-600 text-sm mb-6">
                A good photo helps people connect with your{" "}
                {itemType === "food" ? "food" : "item"}
              </p>
            </div>

            <div className="relative">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
                id="image-upload"
              />
              <div
                className={`border-2 border-dashed rounded-xl p-8 text-center transition-all ${
                  imagePreview
                    ? "border-green-300 bg-green-50"
                    : "border-gray-300 bg-gray-50"
                }`}
              >
                {imagePreview ? (
                  <div className="space-y-4">
                    <Image
                      src={imagePreview}
                      alt="Preview"
                      width={200}
                      height={200}
                      className="mx-auto rounded-lg object-cover"
                    />
                    <p className="text-green-600 font-medium">Perfect! üì∏</p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    <Camera className="w-12 h-12 text-gray-400 mx-auto" />
                    <div>
                      <p className="font-medium text-gray-900">
                        Tap to add photo
                      </p>
                      <p className="text-sm text-gray-500">
                        Optional but recommended
                      </p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}

        {/* Step 2: Details */}
        {currentStep === 2 && (
          <div className="space-y-6 py-6">
            <div className="text-center">
              <h3 className="text-lg font-medium mb-2">
                Tell us about your {itemType}
              </h3>
              <p className="text-gray-600 text-sm mb-6">
                Help others understand what you're sharing
              </p>
            </div>

            <div className="space-y-4">
              <div>
                <Label htmlFor="title" className="text-base font-medium">
                  What are you sharing? *
                </Label>
                <Input
                  id="title"
                  value={formData.title}
                  onChange={(e) =>
                    setFormData({ ...formData, title: e.target.value })
                  }
                  placeholder={
                    itemType === "food"
                      ? "e.g., Fresh tomatoes"
                      : "e.g., Men's running shoes"
                  }
                  className="mt-2 text-base h-12"
                />
              </div>

              <div>
                <Label className="text-base font-medium">Category *</Label>
                <div className="grid grid-cols-2 gap-3 mt-2">
                  {getCategories().map((cat) => (
                    <button
                      key={cat.value}
                      type="button"
                      onClick={() =>
                        setFormData({ ...formData, category: cat.value })
                      }
                      className={`p-4 rounded-xl border-2 text-left transition-all ${
                        formData.category === cat.value
                          ? "border-green-500 bg-green-50"
                          : "border-gray-200 bg-white"
                      }`}
                    >
                      <div className="text-2xl mb-1">{cat.icon}</div>
                      <div className="text-sm font-medium">{cat.label}</div>
                    </button>
                  ))}
                </div>
              </div>

              <div>
                <Label htmlFor="quantity" className="text-base font-medium">
                  Quantity *
                </Label>
                <Input
                  id="quantity"
                  value={formData.quantity}
                  onChange={(e) =>
                    setFormData({ ...formData, quantity: e.target.value })
                  }
                  placeholder={
                    itemType === "food"
                      ? "e.g., 2 kg, 5 pieces"
                      : "e.g., 1 pair, 3 items"
                  }
                  className="mt-2 text-base h-12"
                />
              </div>

              {itemType === "non-food" && (
                <div>
                  <Label className="text-base font-medium">Condition *</Label>
                  <div className="space-y-2 mt-2">
                    {conditions.map((condition) => (
                      <button
                        key={condition.value}
                        type="button"
                        onClick={() =>
                          setFormData({
                            ...formData,
                            condition: condition.value,
                          })
                        }
                        className={`w-full p-3 rounded-lg border text-left transition-all ${
                          formData.condition === condition.value
                            ? "border-green-500 bg-green-50"
                            : "border-gray-200 bg-white"
                        }`}
                      >
                        <div className="font-medium">{condition.label}</div>
                        <div className="text-sm text-gray-600">
                          {condition.description}
                        </div>
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {itemType === "food" && (
                <div>
                  <Label
                    htmlFor="expiry_date"
                    className="text-base font-medium"
                  >
                    Best before (optional)
                  </Label>
                  <Input
                    id="expiry_date"
                    type="date"
                    value={formData.expiry_date}
                    onChange={(e) =>
                      setFormData({ ...formData, expiry_date: e.target.value })
                    }
                    className="mt-2 text-base h-12"
                  />
                </div>
              )}

              <div>
                <Label htmlFor="description" className="text-base font-medium">
                  Additional details (optional)
                </Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) =>
                    setFormData({ ...formData, description: e.target.value })
                  }
                  placeholder="Any special notes..."
                  rows={3}
                  className="mt-2 text-base"
                />
              </div>
            </div>
          </div>
        )}

        {/* Step 3: Location */}
        {currentStep === 3 && (
          <div className="space-y-6 py-6">
            <div className="text-center">
              <h3 className="text-lg font-medium mb-2">
                Where can people collect?
              </h3>
              <p className="text-gray-600 text-sm mb-6">
                Help people find you easily
              </p>
            </div>

            <div className="space-y-4">
              <div>
                <Label
                  htmlFor="pickup_location"
                  className="text-base font-medium"
                >
                  Pickup location *
                </Label>
                <Input
                  id="pickup_location"
                  value={formData.pickup_location}
                  onChange={(e) =>
                    setFormData({
                      ...formData,
                      pickup_location: e.target.value,
                    })
                  }
                  placeholder="e.g., Near Area 25 Market"
                  className="mt-2 text-base h-12"
                />
              </div>

              <div>
                <Label className="text-base font-medium">Area/Township *</Label>
                <Select
                  value={formData.area}
                  onValueChange={(value) =>
                    setFormData({ ...formData, area: value })
                  }
                >
                  <SelectTrigger className="mt-2 text-base h-12">
                    <SelectValue placeholder="Choose your area" />
                  </SelectTrigger>
                  <SelectContent>
                    {areaOptions.map((area) => (
                      <SelectItem key={area} value={area}>
                        {area.charAt(0).toUpperCase() + area.slice(1)}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="bg-blue-50 p-4 rounded-lg">
                <div className="flex items-center space-x-2">
                  <Switch
                    id="is_anonymous"
                    checked={formData.is_anonymous}
                    onCheckedChange={(checked) =>
                      setFormData({ ...formData, is_anonymous: checked })
                    }
                  />
                  <Label htmlFor="is_anonymous" className="text-base">
                    Share anonymously
                  </Label>
                </div>
                <p className="text-sm text-blue-600 mt-2">
                  Your name won't be shown to others
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Step 4: Impact */}
        {currentStep === 4 && (
          <div className="space-y-6 py-6">
            <div className="text-center">
              <h3 className="text-lg font-medium mb-2">Choose your impact</h3>
              <p className="text-gray-600 text-sm mb-6">
                Who would you like to help?
              </p>
            </div>

            <div className="space-y-3">
              <button
                type="button"
                onClick={() =>
                  setFormData({ ...formData, collaboration_id: null })
                }
                className={`w-full p-4 rounded-xl border-2 text-left transition-all ${
                  !formData.collaboration_id
                    ? "border-green-500 bg-green-50"
                    : "border-gray-200 bg-white"
                }`}
              >
                <div className="flex items-center space-x-3">
                  <Globe className="w-6 h-6 text-blue-500" />
                  <div>
                    <div className="font-medium">Public Donation</div>
                    <div className="text-sm text-gray-600">
                      Available to everyone in your area
                    </div>
                  </div>
                </div>
              </button>

              {collaborations.map((collaboration) => (
                <button
                  key={collaboration.id}
                  type="button"
                  onClick={() =>
                    setFormData({
                      ...formData,
                      collaboration_id: collaboration.id,
                    })
                  }
                  className={`w-full p-4 rounded-xl border-2 text-left transition-all ${
                    formData.collaboration_id === collaboration.id
                      ? "border-green-500 bg-green-50"
                      : "border-gray-200 bg-white"
                  }`}
                >
                  <div className="flex items-center space-x-3">
                    <Users className="w-6 h-6 text-purple-500" />
                    <div>
                      <div className="font-medium">{collaboration.title}</div>
                      <div className="text-sm text-gray-600">
                        Specific cause or community project
                      </div>
                    </div>
                  </div>
                </button>
              ))}
            </div>

            {/* Preview Card */}
            <div className="mt-8">
              <h4 className="font-medium mb-3">Preview of your listing:</h4>
              <Card className="border-2 border-green-200">
                <CardContent className="p-4">
                  <div className="flex space-x-3">
                    {imagePreview ? (
                      <Image
                        src={imagePreview}
                        alt="Preview"
                        width={60}
                        height={60}
                        className="rounded-lg object-cover"
                      />
                    ) : (
                      <div className="w-15 h-15 bg-gray-200 rounded-lg flex items-center justify-center">
                        <span className="text-2xl">
                          {getCategoryIcon(formData.category)}
                        </span>
                      </div>
                    )}
                    <div className="flex-1">
                      <h5 className="font-medium">
                        {formData.title || "Your item title"}
                      </h5>
                      <p className="text-sm text-gray-600">
                        {formData.quantity || "Quantity"}
                      </p>
                      <p className="text-sm text-green-600">
                        üìç {formData.area || "Your area"}
                      </p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>
          </div>
        )}
      </div>

      {/* Navigation Buttons */}
      <div className="sticky bottom-0 bg-white border-t border-gray-100 p-4">
        <div className="flex space-x-3">
          {currentStep > 1 && (
            <Button
              variant="outline"
              onClick={handlePrevious}
              className="flex-1 h-12 text-base"
            >
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back
            </Button>
          )}

          {currentStep < 4 ? (
            <Button
              onClick={handleNext}
              disabled={!canProceedToNext()}
              className={`h-12 text-base font-medium ${
                currentStep === 1 ? "w-full" : "flex-1"
              } bg-green-600 hover:bg-green-700 disabled:opacity-50`}
            >
              Continue
              <ArrowRight className="w-4 h-4 ml-2" />
            </Button>
          ) : (
            <Button
              onClick={handleSubmit}
              disabled={loading || !canProceedToNext()}
              className="flex-1 h-12 text-base font-medium bg-green-600 hover:bg-green-700 disabled:opacity-50"
            >
              {loading ? (
                <div className="flex items-center space-x-2">
                  <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                  <span>Sharing...</span>
                </div>
              ) : (
                <div className="flex items-center space-x-2">
                  <Heart className="w-4 h-4" />
                  <span>Share with Community</span>
                </div>
              )}
            </Button>
          )}
        </div>
      </div>
    </div>
  );
}

components/Auth.tsx.content:
"use client"

import type React from "react"

import { useState } from "react"
import { useRouter } from "next/navigation"
import { supabase } from "@/lib/supabase"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Label } from "@/components/ui/label"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { ArrowLeft } from "lucide-react"
import { getCityCoordinates } from "@/lib/locationService"

interface AuthProps {
  onBackToLanding?: () => void
}

export default function Auth({ onBackToLanding }: AuthProps) {
  const [loading, setLoading] = useState(false)
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [fullName, setFullName] = useState("")
  const [phone, setPhone] = useState("")
  const [location, setLocation] = useState("")
  const [activeTab, setActiveTab] = useState("signin")

  const router = useRouter()

  const handleSignUp = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)

    try {
      console.log("Attempting to sign up user:", email)

      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          emailRedirectTo: undefined, // Disable email confirmation redirect
          data: {
            full_name: fullName,
            phone,
            location,
          },
        },
      })

      if (error) {
        console.error("Supabase signup error:", error)
        throw error
      }

      console.log("Signup response:", data)

      // Check if user was created successfully
      if (data.user) {
        console.log("User created successfully:", data.user.id)

        // Wait a moment for the trigger to create the profile
        await new Promise(resolve => setTimeout(resolve, 1000))

        // Verify profile was created
        const { data: profile, error: profileError } = await supabase
          .from('profiles')
          .select('*')
          .eq('id', data.user.id)
          .single()

        if (profileError) {
          console.error("Profile creation error:", profileError)
          // Get location coordinates if location is provided
          let latitude = null;
          let longitude = null;

          if (location) {
            const cityLocation = getCityCoordinates(location);
            if (cityLocation) {
              latitude = cityLocation.latitude;
              longitude = cityLocation.longitude;
            }
          }

          // Try to create profile manually if trigger failed
          const { error: insertError } = await supabase
            .from('profiles')
            .insert({
              id: data.user.id,
              email: data.user.email || email,
              full_name: fullName,
              phone,
              location,
              latitude,
              longitude,
            })

          if (insertError) {
            console.error("Manual profile creation failed:", insertError)
          } else {
            console.log("Profile created manually")
          }
        } else {
          console.log("Profile created by trigger:", profile)
        }

        alert("Account created successfully! Please sign in with your email and password.")

        // Reset form and switch to sign in tab
        setEmail("")
        setPassword("")
        setFullName("")
        setPhone("")
        setLocation("")

        // Switch to sign in tab
        setActiveTab("signin")
      } else {
        throw new Error("User creation failed - no user data returned")
      }
    } catch (error: any) {
      console.error("Sign up error:", error)
      alert(error.message || "Error creating account. Please try again.")
    } finally {
      setLoading(false)
    }
  }

  const handleSignIn = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)

    try {
      const { error } = await supabase.auth.signInWithPassword({
        email,
        password,
      })

      if (error) {
        throw error
      }

      // Refresh session explicitly to avoid stale state
      const { data: sessionData } = await supabase.auth.getSession()

      // Redirect to home/dashboard after login
      router.push("/")
    } catch (error: any) {
      console.error("Sign in error:", error)
      alert(error.message || "Error signing in. Please check your credentials.")
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-green-50 p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          {onBackToLanding && (
            <div className="flex justify-start mb-2">
              <Button
                variant="ghost"
                size="sm"
                onClick={onBackToLanding}
                className="flex items-center gap-2 text-green-600 hover:text-green-700 hover:bg-green-100 p-2 rounded-full"
              >
                <ArrowLeft className="h-4 w-4" />
                <span className="text-sm">Back</span>
              </Button>
            </div>
          )}
          <CardTitle className="text-2xl font-bold text-green-700">ü•ó Malawi Food Share</CardTitle>
          <CardDescription>Share food, reduce waste, help your community</CardDescription>
        </CardHeader>
        <CardContent>
          <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="signin">Sign In</TabsTrigger>
              <TabsTrigger value="signup">Sign Up</TabsTrigger>
            </TabsList>

            <TabsContent value="signin">
              <form onSubmit={handleSignIn} className="space-y-4">
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input id="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>
                <div>
                  <Label htmlFor="password">Password</Label>
                  <Input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <Button type="submit" className="w-full bg-green-600 hover:bg-green-700" disabled={loading}>
                  {loading ? "Signing in..." : "Sign In"}
                </Button>
              </form>
            </TabsContent>

            <TabsContent value="signup">
              <form onSubmit={handleSignUp} className="space-y-4">
                <div>
                  <Label htmlFor="fullName">Full Name</Label>
                  <Input
                    id="fullName"
                    type="text"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input id="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>
                <div>
                  <Label htmlFor="phone">Phone Number</Label>
                  <Input
                    id="phone"
                    type="tel"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                    placeholder="+265..."
                  />
                </div>
                <div>
                  <Label htmlFor="location">Location (City/Area)</Label>
                  <Input
                    id="location"
                    type="text"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    placeholder="e.g., Lilongwe, Blantyre"
                  />
                </div>
                <div>
                  <Label htmlFor="password">Password</Label>
                  <Input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <Button type="submit" className="w-full bg-green-600 hover:bg-green-700" disabled={loading}>
                  {loading ? "Creating account..." : "Sign Up"}
                </Button>
              </form>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  )
}

components/ChatModal.tsx.content:
"use client"

import { useState, useEffect, useRef, useCallback } from "react"
import { supabase } from "@/lib/supabase"
import { useAuth } from "@/contexts/AuthContext"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle
} from "@/components/ui/dialog"
import { ScrollArea } from "@/components/ui/scroll-area"
import { Send, Loader2, X } from "lucide-react"

interface Message {
  id: string
  message: string
  sender_id: string
  created_at: string
  claim_id: string
  sender_name?: string
}

interface ChatModalProps {
  claimId: string | null
  isOpen: boolean
  onClose: () => void
  otherUserName: string
}

export default function ChatModal({ claimId, isOpen, onClose, otherUserName }: ChatModalProps) {
  const { user } = useAuth()
  const [messages, setMessages] = useState<Message[]>([])
  const [newMessage, setNewMessage] = useState("")
  const [loading, setLoading] = useState(false)
  const [sending, setSending] = useState(false)
  const [isTyping, setIsTyping] = useState(false)
  const scrollAreaRef = useRef<HTMLDivElement | null>(null)
  const messagesEndRef = useRef<HTMLDivElement | null>(null)
  const typingTimeoutRef = useRef<NodeJS.Timeout | undefined>(undefined)
  const [requestMessage, setRequestMessage] = useState<string | null>(null)
  const [claimLoading, setClaimLoading] = useState(false)
  const [claimInfo, setClaimInfo] = useState<{ owner_id: string; claimer_id: string; item_id: string; status: string } | null>(null)
  const [completing, setCompleting] = useState(false)

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" })
  }, [])

  const fetchMessages = useCallback(async () => {
    if (!claimId) return
    setLoading(true)
    try {
      const { data, error } = await supabase.rpc("get_messages_with_sender_names", {
        p_claim_id: claimId,
        p_limit: 100,
      })

      if (error) {
        console.error("Error fetching messages:", error)
      } else {
        setMessages(data || [])
        setTimeout(scrollToBottom, 100)
      }
    } catch (error) {
      console.error("Unexpected error fetching messages:", error)
    } finally {
      setLoading(false)
    }
  }, [claimId, scrollToBottom])

  // Real-time listener without broken filters
  useEffect(() => {
    if (!claimId || !isOpen) return

    fetchMessages()

    const channel = supabase
      .channel('realtime:messages')
      .on(
        'postgres_changes',
        {
          event: 'INSERT',
          schema: 'public',
          table: 'messages',
        },
        async (payload) => {
          const newMsg = payload.new as Message

          if (newMsg.claim_id !== claimId) return

          const { data: profile } = await supabase
            .from("profiles")
            .select("full_name")
            .eq("id", newMsg.sender_id)
            .single()

          const messageWithName = {
            ...newMsg,
            sender_name: profile?.full_name || "Anonymous",
          }

          setMessages(prev => {
            if (prev.some(msg => msg.id === newMsg.id)) return prev
            return [...prev, messageWithName]
          })

          setTimeout(scrollToBottom, 100)
        }
      )
      .subscribe()

    return () => {
      channel.unsubscribe()
    }
  }, [claimId, isOpen, fetchMessages, scrollToBottom])

  // Fetch the claim's request message
  useEffect(() => {
    if (!claimId || !isOpen) return
    setClaimLoading(true)
    const fetchClaimMessage = async () => {
      const { data, error } = await supabase
        .from("claims")
        .select("message")
        .eq("id", claimId)
        .single()
      if (!error && data) setRequestMessage(data.message)
      else setRequestMessage(null)
      setClaimLoading(false)
    }
    fetchClaimMessage()
  }, [claimId, isOpen])

  // Fetch claim info for completion logic
  useEffect(() => {
    if (!claimId || !isOpen) return
    const fetchClaimInfo = async () => {
      const { data, error } = await supabase
        .from("claims")
        .select("owner_id, claimer_id, item_id, status")
        .eq("id", claimId)
        .single()
      if (!error && data) setClaimInfo(data)
      else setClaimInfo(null)
    }
    fetchClaimInfo()
  }, [claimId, isOpen])

  const handleTyping = useCallback(() => {
    if (!isTyping) setIsTyping(true)
    if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current)

    typingTimeoutRef.current = setTimeout(() => {
      setIsTyping(false)
    }, 1000)
  }, [isTyping])

  const sendMessage = async () => {
    if (!newMessage.trim() || !claimId || !user) return

    const messageText = newMessage.trim()
    const tempId = `temp-${Date.now()}`

    // Add message immediately to UI for instant feedback
    const tempMessage = {
      id: tempId,
      claim_id: claimId,
      sender_id: user.id,
      message: messageText,
      created_at: new Date().toISOString(),
      sender_name: "You"
    }

    setMessages(prev => [...prev, tempMessage])
    setNewMessage("")
    setTimeout(scrollToBottom, 50)

    setSending(true)
    try {
      const { data, error } = await supabase.from("messages").insert({
        claim_id: claimId,
        sender_id: user.id,
        message: messageText,
      }).select().single()

      if (error) throw error

      // Replace temp message with real one
      setMessages(prev => prev.map(msg =>
        msg.id === tempId ? { ...data, sender_name: "You" } : msg
      ))
    } catch (error: any) {
      console.error("Error sending message:", error)
      // Remove temp message on error
      setMessages(prev => prev.filter(msg => msg.id !== tempId))
      alert(`Failed to send message: ${error.message}`)
      setNewMessage(messageText) // Restore message text
    } finally {
      setSending(false)
    }
  }

  const handleKeyPress = useCallback(
    (e: React.KeyboardEvent) => {
      if (e.key === "Enter" && !e.shiftKey) {
        e.preventDefault()
        sendMessage()
      }
    },
    [sendMessage]
  )

  const handleInputChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      setNewMessage(e.target.value)
      handleTyping()
    },
    [handleTyping]
  )

  const formatTime = useCallback((dateString: string) => {
    const date = new Date(dateString)
    const now = new Date()
    const diffInHours = (now.getTime() - date.getTime()) / (1000 * 60 * 60)

    return diffInHours < 24
      ? date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })
      : date.toLocaleDateString([], { month: "short", day: "numeric" })
  }, [])

  const handleComplete = async () => {
    if (!claimInfo || !user || claimInfo.owner_id !== user.id) return
    setCompleting(true)
    try {
      // Update claim status
      await supabase.from("claims").update({ status: "completed" }).eq("id", claimId)
      // Update item status
      await supabase.from("items").update({ status: "completed" }).eq("id", claimInfo.item_id)
      // Notify claimer
      await supabase.from("notifications").insert({
        user_id: claimInfo.claimer_id,
        type: "status_update",
        title: "Sharing completed",
        message: "The owner has marked this sharing as complete.",
        related_claim_id: claimId,
        is_read: false,
      })
      // Refetch claim info to update UI
      setClaimInfo({ ...claimInfo, status: "completed" })
      alert("Marked as complete. This item will be removed from active listings.")
    } catch (err) {
      alert("Error marking as complete.")
    } finally {
      setCompleting(false)
    }
  }

  if (!claimId) return null

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md h-[600px] flex flex-col bg-white shadow-xl rounded-xl border-2 border-green-100">
        <DialogHeader className="flex flex-row items-center justify-between gap-2 border-b pb-2 mb-2">
          <div className="flex flex-col flex-1">
            <DialogTitle className="text-lg font-bold">Chat with {otherUserName}</DialogTitle>
            <DialogDescription className="text-xs text-gray-500">
              Coordinate pickup details
              {isTyping && <span className="text-green-600 ml-2">‚Ä¢ typing...</span>}
            </DialogDescription>
          </div>
          <Button variant="ghost" size="icon" onClick={onClose} aria-label="Close chat">
            <X className="h-5 w-5" />
          </Button>
        </DialogHeader>

        <ScrollArea className="flex-1 p-4 border rounded bg-gray-50" ref={scrollAreaRef}>
          {claimLoading ? (
            <div className="flex justify-center items-center h-16">
              <Loader2 className="h-5 w-5 animate-spin text-green-600" />
            </div>
          ) : requestMessage ? (
            <div className="mb-4 p-3 rounded-lg bg-green-100 text-green-900 text-sm border border-green-200">
              <span className="font-semibold">Request message:</span>
              <br />
              {requestMessage}
            </div>
          ) : null}
          {loading ? (
            <div className="flex justify-center items-center h-32">
              <Loader2 className="h-6 w-6 animate-spin text-green-600" />
            </div>
          ) : (
            <div className="space-y-4">
              {messages.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <p>No messages yet</p>
                  <p className="text-xs mt-1">Start the conversation!</p>
                </div>
              ) : (
                messages.map((message, index) => {
                  const isOwn = message.sender_id === user?.id
                  const showSender = index === 0 || messages[index - 1].sender_id !== message.sender_id

                  return (
                    <div key={message.id} className={`flex ${isOwn ? "justify-end" : "justify-start"}`}>
                      <div
                        className={`max-w-[80%] p-3 rounded-2xl shadow-sm transition-all duration-200 text-sm break-words
                          ${isOwn ? "bg-green-600 text-white rounded-br-md" : "bg-white border border-gray-200 text-gray-900 rounded-bl-md"}
                        `}
                      >
                        {!isOwn && showSender && (
                          <p className="text-xs font-medium mb-1 opacity-70">{message.sender_name}</p>
                        )}
                        <p className="whitespace-pre-wrap">{message.message}</p>
                        <p className="text-xs opacity-60 mt-1 text-right">{formatTime(message.created_at)}</p>
                      </div>
                    </div>
                  )
                })
              )}
              <div ref={messagesEndRef} />
            </div>
          )}
        </ScrollArea>

        <form
          className="flex gap-2 mt-2"
          onSubmit={e => {
            e.preventDefault();
            sendMessage();
          }}
        >
          <Input
            value={newMessage}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
            placeholder="Type your message..."
            disabled={sending}
            className="flex-1 focus:ring-2 focus:ring-green-400 focus:border-green-400 rounded-full px-4 py-2 text-base"
            maxLength={500}
            autoFocus
          />
          <Button type="submit" onClick={sendMessage} disabled={sending || !newMessage.trim()} size="icon" className="shrink-0 bg-green-600 hover:bg-green-700">
            {sending ? <Loader2 className="h-4 w-4 animate-spin" /> : <Send className="h-4 w-4" />}
          </Button>
        </form>
        {claimInfo && (claimInfo.owner_id === user?.id || claimInfo.claimer_id === user?.id) && claimInfo.status !== "completed" && (
          <Button
            onClick={handleComplete}
            disabled={completing}
            className="mt-2 bg-blue-600 hover:bg-blue-700 w-full"
          >
            {completing ? "Completing..." : "Mark as Shared"}
          </Button>
        )}
      </DialogContent>
    </Dialog>
  )
}

components/ClaimFoodModal.tsx.content:
"use client"

import { useState } from "react"
import { supabase } from "@/lib/supabase"
import { useAuth } from "@/contexts/AuthContext"
import { Button } from "@/components/ui/button"
import { Textarea } from "@/components/ui/textarea"
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog"
import { Label } from "@/components/ui/label"

interface Item {
  id: string
  title: string
  description: string | null
  pickup_location: string
  user_id: string
  owner_name?: string | null
}

interface ClaimFoodModalProps {
  foodItem: Item | null
  isOpen: boolean
  onClose: () => void
  onClaimed: () => void
}

export default function ClaimFoodModal({ foodItem, isOpen, onClose, onClaimed }: ClaimFoodModalProps) {
  const { user } = useAuth()
  const [message, setMessage] = useState("")
  const [loading, setLoading] = useState(false)

  const handleClaim = async () => {
    if (!user || !foodItem) return

    setLoading(true)

    try {
      console.log("Creating claim for item:", foodItem.id)

      const { data, error } = await supabase
        .from("claims")
        .insert({
          item_id: foodItem.id,
          claimer_id: user.id,
          owner_id: foodItem.user_id,
          message: message || null,
        })
        .select()

      if (error) {
        console.error("Error creating claim:", error)
        throw error
      }

      console.log("Claim created successfully:", data)

      // Update item status to 'requested' instead of 'claimed'
      const { error: updateError } = await supabase.from("items").update({ status: "requested" }).eq("id", foodItem.id)

      if (updateError) {
        console.warn("Error updating item status:", updateError)
      } else {
        console.log("Item status updated to requested")
      }

      onClaimed()
      onClose()
      setMessage("")

      // Create a temporary success notification
      const successDiv = document.createElement('div')
      successDiv.className = 'fixed top-4 right-4 bg-green-500 text-white px-6 py-3 rounded-lg shadow-lg z-50 animate-pulse'
      successDiv.textContent = '‚úÖ Request sent successfully! Owner will be notified.'
      document.body.appendChild(successDiv)

      setTimeout(() => {
        if (document.body.contains(successDiv)) {
          document.body.removeChild(successDiv)
        }
      }, 4000)
    } catch (error: any) {
      console.error("Error claiming item:", error)
      alert(`Error sending request: ${error.message}`)
    } finally {
      setLoading(false)
    }
  }

  if (!foodItem) return null

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>Request Item</DialogTitle>
          <DialogDescription>
            Send a request to {foodItem.owner_name || "the owner"} for "{foodItem.title}"
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          <div>
            <p className="text-sm text-gray-600">
              <strong>Pickup Location:</strong> {foodItem.pickup_location}
            </p>
          </div>
          <div className="bg-yellow-50 border-l-4 border-yellow-400 p-3 rounded mb-2">
            <div className="font-semibold text-yellow-800 mb-1 text-sm">Safety Tips:</div>
            <ul className="list-disc list-inside text-xs text-yellow-900 space-y-1">
              <li>Meet during the day</li>
              <li>Meet in public places</li>
              <li>Go with a friend</li>
              <li>Keep it cash free</li>
              <li>Trust your instincts and report suspicious activity</li>
            </ul>
          </div>
          <div>
            <Label htmlFor="message">Message (optional)</Label>
            <Textarea
              id="message"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Hi! I'm interested in this item. When can I pick it up?"
              rows={3}
            />
          </div>

          <div className="flex gap-2">
            <Button variant="outline" onClick={onClose} className="flex-1">
              Cancel
            </Button>
            <Button onClick={handleClaim} disabled={loading} className="flex-1 bg-green-600 hover:bg-green-700">
              {loading ? "Sending..." : "Send Request"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  )
}

components/CollaborationCenter.tsx.content:
"use client"

import type React from "react"

import { useState, useEffect } from "react"
import { supabase } from "@/lib/supabase"
import { useAuth } from "@/contexts/AuthContext"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { Textarea } from "@/components/ui/textarea"
import { Label } from "@/components/ui/label"
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog"
import { Badge } from "@/components/ui/badge"
import { Users, Plus, MapPin, Calendar, MessageCircle, AlertCircle, Package, Utensils, Gift } from "lucide-react"

interface Collaboration {
  id: string
  title: string
  description: string
  location: string
  target_date: string | null
  status: string
  created_at: string
  creator_id: string
  creator_name?: string
  participant_count?: number
  is_participant?: boolean
  donation_preview?: {
    food_count: number
    item_count: number
    total_count: number
  }
}

interface CollaborationCenterProps {
  onOpenCollaborationChat: (collaborationId: string, title: string) => void
}

export default function CollaborationCenter({ onOpenCollaborationChat }: CollaborationCenterProps) {
  const { user } = useAuth()
  const [collaborations, setCollaborations] = useState<Collaboration[]>([])
  const [showCreateForm, setShowCreateForm] = useState(false)
  const [loading, setLoading] = useState(false)
  const [isTableMissing, setIsTableMissing] = useState(false)
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    location: "",
    target_date: "",
  })

  useEffect(() => {
    fetchCollaborations()
  }, [])

  const fetchCollaborations = async () => {
    try {
      // First, fetch collaboration requests
      const { data: collaborationData, error: collaborationError } = await supabase
        .from("collaboration_requests")
        .select("*")
        .eq("status", "active")
        .order("created_at", { ascending: false })

      if (collaborationError) {
        console.error("Error fetching collaborations:", collaborationError)
        if (collaborationError.message.includes('relation "public.collaboration_requests" does not exist')) {
          setIsTableMissing(true)
        }
        return
      }

      if (!collaborationData || collaborationData.length === 0) {
        setCollaborations([])
        setIsTableMissing(false)
        return
      }

      // Get creator names separately
      const creatorIds = [...new Set(collaborationData.map((collab) => collab.creator_id))]
      const { data: profilesData } = await supabase.from("profiles").select("id, full_name").in("id", creatorIds)

      // Create a map of creator IDs to names
      const profilesMap = new Map()
      if (profilesData) {
        profilesData.forEach((profile) => {
          profilesMap.set(profile.id, profile.full_name)
        })
      }

      // Optimize database queries by batching them
      const collaborationIds = collaborationData.map(collab => collab.id)

      // Get all participants for all collaborations in one query
      const { data: allParticipants } = await supabase
        .from("collaboration_participants")
        .select("collaboration_id, user_id")
        .in("collaboration_id", collaborationIds)

      // Get all donation data for all collaborations in one query
      const { data: allDonations } = await supabase
        .from("items")
        .select("collaboration_id, item_type")
        .in("collaboration_id", collaborationIds)
        .eq("status", "available")

      // Process the data efficiently
      const participantsByCollab = new Map()
      const donationsByCollab = new Map()

      // Group participants by collaboration
      if (allParticipants) {
        allParticipants.forEach(p => {
          if (!participantsByCollab.has(p.collaboration_id)) {
            participantsByCollab.set(p.collaboration_id, [])
          }
          participantsByCollab.get(p.collaboration_id).push(p.user_id)
        })
      }

      // Group donations by collaboration and type
      if (allDonations) {
        allDonations.forEach(d => {
          if (!donationsByCollab.has(d.collaboration_id)) {
            donationsByCollab.set(d.collaboration_id, { food: 0, nonFood: 0, total: 0 })
          }
          const stats = donationsByCollab.get(d.collaboration_id)
          stats.total++
          if (d.item_type === "food") {
            stats.food++
          } else {
            stats.nonFood++
          }
        })
      }

      // Build the final collaboration objects
      const collaborationsWithDetails = collaborationData.map(collab => {
        const participants = participantsByCollab.get(collab.id) || []
        const donations = donationsByCollab.get(collab.id) || { food: 0, nonFood: 0, total: 0 }
        const isParticipant = user ? participants.includes(user.id) : false

        return {
          ...collab,
          creator_name: profilesMap.get(collab.creator_id) || "Anonymous",
          participant_count: participants.length,
          is_participant: isParticipant,
          donation_preview: {
            food_count: donations.food,
            item_count: donations.nonFood,
            total_count: donations.total
          }
        }
      })

      setCollaborations(collaborationsWithDetails)
      setIsTableMissing(false)
    } catch (error) {
      console.error("Unexpected error fetching collaborations:", error)
      setIsTableMissing(true)
    }
  }

  const createCollaboration = async (e: React.FormEvent) => {
    e.preventDefault()
    if (!user || isTableMissing) return

    setLoading(true)

    try {
      const { data, error } = await supabase
        .from("collaboration_requests")
        .insert({
          creator_id: user.id,
          title: formData.title,
          description: formData.description,
          location: formData.location,
          target_date: formData.target_date || null,
        })
        .select()
        .single()

      if (error) throw error

      // Auto-join the creator as a participant
      await supabase.from("collaboration_participants").insert({
        collaboration_id: data.id,
        user_id: user.id,
      })

      setFormData({ title: "", description: "", location: "", target_date: "" })
      setShowCreateForm(false)
      fetchCollaborations()
      alert("Collaboration created successfully!")
    } catch (error: any) {
      console.error("Error creating collaboration:", error)
      alert(`Error creating collaboration: ${error.message}`)
    } finally {
      setLoading(false)
    }
  }

  const joinCollaboration = async (collaborationId: string) => {
    if (!user || isTableMissing) return

    try {
      const { error } = await supabase.from("collaboration_participants").insert({
        collaboration_id: collaborationId,
        user_id: user.id,
      })

      if (error) throw error

      fetchCollaborations()
      alert("Successfully joined the collaboration!")
    } catch (error: any) {
      console.error("Error joining collaboration:", error)
      alert(`Error joining collaboration: ${error.message}`)
    }
  }

  const leaveCollaboration = async (collaborationId: string) => {
    if (!user || isTableMissing) return

    try {
      const { error } = await supabase
        .from("collaboration_participants")
        .delete()
        .eq("collaboration_id", collaborationId)
        .eq("user_id", user.id)

      if (error) throw error

      fetchCollaborations()
      alert("Left the collaboration")
    } catch (error: any) {
      console.error("Error leaving collaboration:", error)
      alert(`Error leaving collaboration: ${error.message}`)
    }
  }

  // Show setup message if table is missing
  if (isTableMissing) {
    return (
      <div className="space-y-6">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Community Collaborations</h2>
          <p className="text-gray-600">Organize big moves, donations, and community projects</p>
        </div>

        <Card className="max-w-2xl mx-auto">
          <CardContent className="p-8 text-center">
            <AlertCircle className="h-12 w-12 mx-auto mb-4 text-orange-500" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Setup Required</h3>
            <p className="text-gray-600 mb-4">
              The collaboration system needs to be set up. Please run the complete database setup script to enable
              collaboration features.
            </p>
            <div className="bg-gray-100 p-3 rounded text-sm mb-4">
              <strong>Script to run:</strong>
              <br />
              <code>scripts/setup-complete-database.sql</code>
            </div>
            <Button onClick={fetchCollaborations} variant="outline">
              Check Again
            </Button>
          </CardContent>
        </Card>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Community Collaborations</h2>
          <p className="text-gray-600">Organize big moves, donations, and community projects</p>
        </div>
        <Dialog open={showCreateForm} onOpenChange={setShowCreateForm}>
          <DialogTrigger asChild>
            <Button className="bg-green-600 hover:bg-green-700">
              <Plus className="h-4 w-4 mr-2" />
              Start Collaboration
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>Start a New Collaboration</DialogTitle>
            </DialogHeader>
            <form onSubmit={createCollaboration} className="space-y-4">
              <div>
                <Label htmlFor="title">Title *</Label>
                <Input
                  id="title"
                  value={formData.title}
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                  placeholder="e.g., Community Food Drive, Moving Help"
                  required
                />
              </div>
              <div>
                <Label htmlFor="description">Description *</Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  placeholder="Describe what help you need or what you're organizing..."
                  rows={3}
                  required
                />
              </div>
              <div>
                <Label htmlFor="location">Location *</Label>
                <Input
                  id="location"
                  value={formData.location}
                  onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                  placeholder="e.g., Lilongwe, Area 25"
                  required
                />
              </div>
              <div>
                <Label htmlFor="target_date">Target Date (optional)</Label>
                <Input
                  id="target_date"
                  type="date"
                  value={formData.target_date}
                  onChange={(e) => setFormData({ ...formData, target_date: e.target.value })}
                />
              </div>
              <div className="flex gap-2">
                <Button type="button" variant="outline" onClick={() => setShowCreateForm(false)} className="flex-1">
                  Cancel
                </Button>
                <Button type="submit" disabled={loading} className="flex-1 bg-green-600 hover:bg-green-700">
                  {loading ? "Creating..." : "Create"}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {collaborations.map((collab) => (
          <Card key={collab.id} className="hover:shadow-lg transition-all duration-300 border-l-4 border-l-green-500">
            <CardHeader className="pb-3">
              <div className="flex justify-between items-start">
                <CardTitle className="text-lg text-green-700">{collab.title}</CardTitle>
                <Badge variant="secondary" className="bg-blue-100 text-blue-800">
                  <Users className="h-3 w-3 mr-1" />
                  {collab.participant_count}
                </Badge>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-gray-600 text-sm line-clamp-2">{collab.description}</p>

              {/* Simple Donation Summary */}
              {collab.donation_preview && collab.donation_preview.total_count > 0 && (
                <div className="bg-green-50 border border-green-200 rounded-lg p-3">
                  <div className="flex items-center justify-between">
                    <h4 className="text-sm font-medium text-green-800 flex items-center gap-1">
                      <Gift className="h-4 w-4" />
                      Shared Items
                    </h4>
                    <Badge variant="secondary" className="bg-green-100 text-green-800 text-xs">
                      {collab.donation_preview.total_count} total
                    </Badge>
                  </div>

                  <div className="flex gap-4 text-xs text-green-700 mt-2">
                    {collab.donation_preview.food_count > 0 && (
                      <div className="flex items-center gap-1">
                        <Utensils className="h-3 w-3" />
                        <span>{collab.donation_preview.food_count} food</span>
                      </div>
                    )}
                    {collab.donation_preview.item_count > 0 && (
                      <div className="flex items-center gap-1">
                        <Package className="h-3 w-3" />
                        <span>{collab.donation_preview.item_count} items</span>
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Simple Participants Summary */}
              {collab.participant_count && collab.participant_count > 0 && (
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
                  <div className="flex items-center justify-between">
                    <h4 className="text-sm font-medium text-blue-800 flex items-center gap-1">
                      <Users className="h-4 w-4" />
                      Participants
                    </h4>
                    <Badge variant="secondary" className="bg-blue-100 text-blue-800 text-xs">
                      {collab.participant_count} total
                    </Badge>
                  </div>
                </div>
              )}

              <div className="space-y-2">
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <MapPin className="h-4 w-4" />
                  <span>{collab.location}</span>
                </div>

                {collab.target_date && (
                  <div className="flex items-center gap-2 text-sm text-gray-600">
                    <Calendar className="h-4 w-4" />
                    <span>Target: {new Date(collab.target_date).toLocaleDateString()}</span>
                  </div>
                )}
              </div>

              <div className="text-sm text-gray-500 border-t pt-2">
                Created by {collab.creator_name} ‚Ä¢ {new Date(collab.created_at).toLocaleDateString()}
              </div>

              <div className="flex gap-2 pt-2">
                {collab.is_participant ? (
                  <>
                    <Button
                      onClick={() => onOpenCollaborationChat(collab.id, collab.title)}
                      className="flex-1 bg-green-600 hover:bg-green-700"
                      size="sm"
                    >
                      <MessageCircle className="h-4 w-4 mr-1" />
                      Chat
                    </Button>
                    <Button onClick={() => leaveCollaboration(collab.id)} variant="outline" size="sm">
                      Leave
                    </Button>
                  </>
                ) : (
                  <Button
                    onClick={() => joinCollaboration(collab.id)}
                    className="w-full bg-blue-600 hover:bg-blue-700"
                    size="sm"
                  >
                    Join Collaboration
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {collaborations.length === 0 && !isTableMissing && (
        <div className="text-center py-12">
          <Users className="h-12 w-12 mx-auto mb-4 text-gray-400" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No active collaborations</h3>
          <p className="text-gray-600 mb-4">Be the first to start a community collaboration!</p>
          <Button onClick={() => setShowCreateForm(true)} className="bg-green-600 hover:bg-green-700">
            <Plus className="h-4 w-4 mr-2" />
            Start Collaboration
          </Button>
        </div>
      )}
    </div>
  )
}

components/CollaborationChatModal.tsx.content:
"use client"

import type React from "react"

import { useState, useEffect, useRef } from "react"
import { supabase } from "@/lib/supabase"
import { useAuth } from "@/contexts/AuthContext"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog"
import { ScrollArea } from "@/components/ui/scroll-area"
import { Badge } from "@/components/ui/badge"
import { Send, Users, Package, Utensils, Gift, ExternalLink, Clock, MapPin, Calendar, MessageCircle, Loader2 } from "lucide-react"
import { useRouter } from "next/navigation"

interface CollaborationMessage {
  id: string
  message: string
  sender_id: string
  created_at: string
  sender_name?: string
}

interface Participant {
  id: string
  user_id: string
  full_name: string | null
}

interface CollaborationChatModalProps {
  collaborationId: string | null
  collaborationTitle: string
  isOpen: boolean
  onClose: () => void
}

interface DonationSummary {
  food_count: number
  item_count: number
  total_count: number
  recent_donations: Array<{
    id: string
    title: string
    item_type: string
    user_name?: string
    created_at: string
  }>
}

export default function CollaborationChatModal({
  collaborationId,
  collaborationTitle,
  isOpen,
  onClose,
}: CollaborationChatModalProps) {
  const { user } = useAuth()
  const router = useRouter()
  const [messages, setMessages] = useState<CollaborationMessage[]>([])
  const [participants, setParticipants] = useState<Participant[]>([])
  const [donationSummary, setDonationSummary] = useState<DonationSummary | null>(null)
  const [newMessage, setNewMessage] = useState("")
  const [loading, setLoading] = useState(false)
  const [isNavigating, setIsNavigating] = useState(false)
  const scrollAreaRef = useRef<HTMLDivElement>(null)

  useEffect(() => {
    if (collaborationId && isOpen) {
      fetchMessages()
      fetchParticipants()
      fetchDonationSummary()

      // Subscribe to new messages
      const subscription = supabase
        .channel(`collaboration_messages:${collaborationId}`)
        .on(
          "postgres_changes",
          {
            event: "INSERT",
            schema: "public",
            table: "collaboration_messages",
            filter: `collaboration_id=eq.${collaborationId}`,
          },
          (payload) => {
            const newMsg = payload.new as CollaborationMessage
            // Fetch sender name for the new message
            fetchSenderName(newMsg.sender_id).then((senderName) => {
              setMessages((prev) => [...prev, { ...newMsg, sender_name: senderName }])
            })
          },
        )
        .subscribe()

      return () => {
        subscription.unsubscribe()
      }
    }
  }, [collaborationId, isOpen])

  useEffect(() => {
    // Scroll to bottom when new messages arrive
    if (scrollAreaRef.current) {
      const scrollElement = scrollAreaRef.current.querySelector('[data-radix-scroll-area-viewport]')
      if (scrollElement) {
        scrollElement.scrollTop = scrollElement.scrollHeight
      }
    }
  }, [messages])

  const fetchSenderName = async (senderId: string): Promise<string> => {
    const { data } = await supabase.from("profiles").select("full_name").eq("id", senderId).single()
    return data?.full_name || "Anonymous"
  }

  const fetchMessages = async () => {
    if (!collaborationId) return

    const { data, error } = await supabase
      .from("collaboration_messages")
      .select("*")
      .eq("collaboration_id", collaborationId)
      .order("created_at", { ascending: true })

    if (error) {
      console.error("Error fetching messages:", error)
    } else {
      // Get sender names for all messages
      const messagesWithNames = await Promise.all(
        (data || []).map(async (msg) => {
          const senderName = await fetchSenderName(msg.sender_id)
          return {
            ...msg,
            sender_name: senderName,
          }
        }),
      )
      setMessages(messagesWithNames)
    }
  }

  const fetchParticipants = async () => {
    if (!collaborationId) return

    // First get participant user IDs
    const { data: participantData, error } = await supabase
      .from("collaboration_participants")
      .select("*")
      .eq("collaboration_id", collaborationId)

    if (error) {
      console.error("Error fetching participants:", error)
      return
    }

    if (!participantData || participantData.length === 0) {
      setParticipants([])
      return
    }

    // Then get their profile information
    const userIds = participantData.map((p) => p.user_id)
    const { data: profilesData } = await supabase.from("profiles").select("id, full_name").in("id", userIds)

    // Combine participant and profile data
    const participantsWithProfiles = participantData.map((participant) => {
      const profile = profilesData?.find((p) => p.id === participant.user_id)
      return {
        ...participant,
        full_name: profile?.full_name || null,
      }
    })

    setParticipants(participantsWithProfiles)
  }

  const fetchDonationSummary = async () => {
    if (!collaborationId) return

    try {
      // Get items for this collaboration - show all items regardless of location
      const { data: donationData, error } = await supabase
        .from("items")
        .select("id, title, item_type, user_id, created_at")
        .eq("status", "available")
        .eq("collaboration_id", collaborationId)
        .order("created_at", { ascending: false })
        .limit(10)

      if (error) {
        console.error("Error fetching donations:", error)
        setDonationSummary({
          food_count: 0,
          item_count: 0,
          total_count: 0,
          recent_donations: []
        })
        return
      }

      if (!donationData || donationData.length === 0) {
        setDonationSummary({
          food_count: 0,
          item_count: 0,
          total_count: 0,
          recent_donations: []
        })
        return
      }

      // Process data efficiently without additional queries
      const foodCount = donationData.filter(item => item.item_type === "food").length
      const itemCount = donationData.filter(item => item.item_type === "non-food").length

      const recentDonations = donationData.map(item => ({
        id: item.id,
        title: item.title,
        item_type: item.item_type,
        user_name: (item.profiles as any)?.full_name || "Anonymous",
        created_at: item.created_at
      }))

      setDonationSummary({
        food_count: foodCount,
        item_count: itemCount,
        total_count: donationData.length,
        recent_donations: recentDonations
      })
    } catch (error) {
      console.error("Error fetching donation summary:", error)
      setDonationSummary({
        food_count: 0,
        item_count: 0,
        total_count: 0,
        recent_donations: []
      })
    }
  }

  const sendMessage = async () => {
    if (!user || !collaborationId || !newMessage.trim()) return

    const messageText = newMessage.trim()
    setNewMessage("")

    // Optimistically add message to UI
    const optimisticMessage = {
      id: `temp-${Date.now()}`,
      message: messageText,
      sender_id: user.id,
      created_at: new Date().toISOString(),
      sender_name: user.user_metadata?.full_name || 'You'
    }

    setMessages(prev => [...prev, optimisticMessage])

    setLoading(true)

    try {
      const { data, error } = await supabase.from("collaboration_messages").insert({
        collaboration_id: collaborationId,
        sender_id: user.id,
        message: messageText,
      }).select()

      if (error) {
        throw error
      }

      // Replace optimistic message with real one
      if (data && data[0]) {
        setMessages(prev => prev.map(msg => 
          msg.id === optimisticMessage.id ? { ...data[0], sender_name: optimisticMessage.sender_name } : msg
        ))
      }
    } catch (error: any) {
      console.error("Error sending message:", error)
      // Remove optimistic message on error
      setMessages(prev => prev.filter(msg => msg.id !== optimisticMessage.id))
      alert(`Error sending message: ${error.message}`)
    } finally {
      setLoading(false)
    }
  }

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault()
      sendMessage()
    }
  }

  const formatTime = (dateString: string) => {
    const date = new Date(dateString)
    const now = new Date()
    const diff = now.getTime() - date.getTime()
    const minutes = Math.floor(diff / 60000)
    const hours = Math.floor(minutes / 60)
    const days = Math.floor(hours / 24)

    if (minutes < 1) return "Just now"
    if (minutes < 60) return `${minutes}m ago`
    if (hours < 24) return `${hours}h ago`
    if (days < 7) return `${days}d ago`
    return date.toLocaleDateString()
  }

  const handleNavigateToFullPage = () => {
    setIsNavigating(true)
    onClose() // Close the modal first
    // Use Next.js router for instant navigation within the app
    router.push(`/collaborations/${collaborationId}`)
    setIsNavigating(false)
  }

  if (!collaborationId) return null

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[95vh] h-[95vh] p-0 m-2 sm:m-6">
        <DialogHeader className="border-b pb-4 px-4 sm:px-6 pt-4 sm:pt-6 bg-white rounded-t-lg">
          <div className="flex items-center justify-between gap-3">
            <div className="flex items-center gap-2 sm:gap-3 flex-1 min-w-0">
              <div className="w-10 h-10 sm:w-12 sm:h-12 bg-green-100 rounded-full flex items-center justify-center flex-shrink-0">
                <Users className="h-5 w-5 sm:h-6 sm:w-6 text-green-600" />
              </div>
              <div className="flex-1 min-w-0">
                <DialogTitle className="text-lg sm:text-xl font-bold text-gray-900 truncate">{collaborationTitle}</DialogTitle>
                <DialogDescription className="text-xs sm:text-sm text-gray-600 mt-1">
                  Chat with participants and view collaboration items
                </DialogDescription>
                <div className="flex flex-wrap items-center gap-1 sm:gap-2 mt-1 sm:mt-2">
                  <Badge variant="secondary" className="text-xs bg-blue-100 text-blue-800 px-2 py-1">
                    <Users className="h-3 w-3 mr-1" />
                    {participants.length}
                  </Badge>
                  {donationSummary && donationSummary.total_count > 0 && (
                    <Badge variant="secondary" className="text-xs bg-green-100 text-green-800 px-2 py-1">
                      <Gift className="h-3 w-3 mr-1" />
                      {donationSummary.total_count}
                    </Badge>
                  )}
                </div>
              </div>
            </div>
            <Button
              className="bg-green-600 hover:bg-green-700 text-white font-medium px-3 py-2 sm:px-4 sm:py-3 flex-shrink-0 min-h-[44px] rounded-xl touch-target-lg"
              onClick={handleNavigateToFullPage}
              disabled={isNavigating}
            >
              {isNavigating ? (
                <Loader2 className="h-4 w-4 animate-spin" />
              ) : (
                <>
                  <ExternalLink className="h-4 w-4 sm:mr-2" />
                  <span className="hidden sm:inline">View Full Page</span>
                </>
              )}
            </Button>
          </div>
        </DialogHeader>

        <div className="flex-1 flex flex-col min-h-0">
          {/* Chat Area */}
          <div className="flex-1 px-6">
            <ScrollArea className="h-96 p-4" ref={scrollAreaRef}>
              <div className="space-y-4">
                {messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex gap-3 ${
                      message.sender_id === user?.id ? "justify-end" : "justify-start"
                    }`}
                  >
                    {message.sender_id !== user?.id && (
                      <div className="w-8 h-8 bg-gray-200 rounded-full flex items-center justify-center text-sm font-medium">
                        {message.sender_name?.charAt(0) || "U"}
                      </div>
                    )}
                    <div
                      className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                        message.sender_id === user?.id
                          ? "bg-green-600 text-white"
                          : "bg-gray-100 text-gray-900"
                      }`}
                    >
                      <p className="text-xs font-medium mb-1 opacity-80">
                        {message.sender_id === user?.id ? "You" : (message.sender_name || "Unknown User")}
                      </p>
                      <p className="text-sm">{message.message}</p>
                      <p className="text-xs opacity-70 mt-1">
                        {formatTime(message.created_at)}
                      </p>
                    </div>
                    {message.sender_id === user?.id && (
                      <div className="w-8 h-8 bg-green-600 rounded-full flex items-center justify-center text-sm font-medium text-white">
                        {message.sender_name?.charAt(0) || "U"}
                      </div>
                    )}
                  </div>
                ))}
                {messages.length === 0 && (
                  <div className="text-center text-gray-500 py-8">
                    <MessageCircle className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                    <p>No messages yet. Start the conversation!</p>
                  </div>
                )}
              </div>
            </ScrollArea>
          </div>

          {/* Message Input */}
          <div className="border-t p-4 sm:p-6 bg-white rounded-b-lg">
            <form onSubmit={(e) => {
              e.preventDefault();
              sendMessage();
            }} className="flex gap-3">
              <Input
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
                className="flex-1 py-3 px-4 text-base sm:text-sm rounded-xl border-2 border-gray-200 focus:border-green-500 transition-colors min-h-[48px] mobile-text-base"
                disabled={loading}
              />
              <Button 
                type="submit" 
                disabled={loading || !newMessage.trim()}
                className="bg-green-600 hover:bg-green-700 px-4 py-3 min-h-[48px] min-w-[48px] rounded-xl touch-target-lg"
              >
                {loading ? (
                  <Loader2 className="h-5 w-5 animate-spin" />
                ) : (
                  <Send className="h-5 w-5" />
                )}
              </Button>
            </form>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  )
}

components/DatabaseTest.tsx.content:
"use client"

import { useState } from "react"
import { supabase } from "@/lib/supabase"
import { useAuth } from "@/contexts/AuthContext"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"

export default function DatabaseTest() {
  const { user } = useAuth()
  const [testing, setTesting] = useState(false)
  const [results, setResults] = useState<string[]>([])

  const runTests = async () => {
    setTesting(true)
    const testResults: string[] = []

    try {
      // Test 1: Check connection
      testResults.push("üîó Testing database connection...")
      const { data: connectionTest, error: connectionError } = await supabase.from("profiles").select("count").limit(1)

      if (connectionError) {
        testResults.push(`‚ùå Connection failed: ${connectionError.message}`)
      } else {
        testResults.push("‚úÖ Database connection successful")
      }

      // Test 2: Check items table
      testResults.push("üì¶ Testing items table...")
      const { data: itemsTest, error: itemsError } = await supabase.from("items").select("id").limit(1)

      if (itemsError) {
        testResults.push(`‚ùå Items table error: ${itemsError.message}`)

        // Try food_items table as fallback
        testResults.push("üîÑ Trying food_items table...")
        const { data: foodTest, error: foodError } = await supabase.from("food_items").select("id").limit(1)

        if (foodError) {
          testResults.push(`‚ùå Food_items table error: ${foodError.message}`)
        } else {
          testResults.push("‚úÖ Food_items table accessible")
        }
      } else {
        testResults.push("‚úÖ Items table accessible")
      }

      // Test 3: Check storage
      testResults.push("üñºÔ∏è Testing storage...")
      const { data: storageTest, error: storageError } = await supabase.storage
        .from("item-images")
        .list("", { limit: 1 })

      if (storageError) {
        testResults.push(`‚ùå Storage error: ${storageError.message}`)
      } else {
        testResults.push("‚úÖ Storage accessible")
      }

      // Test 4: Check user authentication
      testResults.push("üë§ Testing user authentication...")
      if (user) {
        testResults.push(`‚úÖ User authenticated: ${user.email}`)
      } else {
        testResults.push("‚ùå User not authenticated")
      }
    } catch (error: any) {
      testResults.push(`üí• Test failed: ${error.message}`)
    }

    setResults(testResults)
    setTesting(false)
  }

  return (
    <Card className="max-w-2xl mx-auto">
      <CardHeader>
        <CardTitle>Database Connection Test</CardTitle>
      </CardHeader>
      <CardContent>
        <Button onClick={runTests} disabled={testing} className="mb-4">
          {testing ? "Running Tests..." : "Run Database Tests"}
        </Button>

        {results.length > 0 && (
          <div className="space-y-2">
            {results.map((result, index) => (
              <div key={index} className="p-2 bg-gray-100 rounded text-sm font-mono">
                {result}
              </div>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  )
}

components/EditItemModal.tsx.content:
"use client"

import type React from "react"
import { useState, useEffect } from "react"
import { supabase } from "@/lib/supabase"
import { useAuth } from "@/contexts/AuthContext"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Textarea } from "@/components/ui/textarea"
import { Label } from "@/components/ui/label"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog"

interface Item {
  id: string
  title: string
  description: string | null
  category: string
  item_type: "food" | "non-food"
  quantity: string
  condition?: string | null
  expiry_date: string | null
  pickup_location: string
  image_url: string | null
  status: string
  created_at: string
  user_id: string
  owner_name?: string | null
}

interface EditItemModalProps {
  item: Item | null
  isOpen: boolean
  onClose: () => void
  onItemUpdated: () => void
}

export default function EditItemModal({ item, isOpen, onClose, onItemUpdated }: EditItemModalProps) {
  const { user } = useAuth()
  const [loading, setLoading] = useState(false)
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
    quantity: "",
    condition: "",
    expiry_date: "",
    pickup_location: "",
    status: "",
  })
  const [imageFile, setImageFile] = useState<File | null>(null)

  useEffect(() => {
    if (item) {
      setFormData({
        title: item.title || "",
        description: item.description || "",
        category: item.category || "",
        quantity: item.quantity || "",
        condition: item.condition || "",
        expiry_date: item.expiry_date || "",
        pickup_location: item.pickup_location || "",
        status: item.status || "available",
      })
    }
  }, [item])

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (!user || !item) {
      alert("Unable to update item")
      return
    }

    // Validate required fields
    if (!formData.title.trim()) {
      alert("Please enter a title")
      return
    }
    if (!formData.category) {
      alert("Please select a category")
      return
    }
    if (!formData.quantity.trim()) {
      alert("Please enter quantity")
      return
    }
    if (!formData.pickup_location.trim()) {
      alert("Please enter pickup location")
      return
    }
    if (item.item_type === "non-food" && !formData.condition) {
      alert("Please select condition")
      return
    }

    console.log("Starting item update...")
    console.log("Form data:", formData)
    console.log("Item ID:", item.id)

    setLoading(true)

    try {
      let imageUrl = item.image_url

      // Upload new image if provided
      if (imageFile) {
        console.log("Uploading new image...")
        const fileExt = imageFile.name.split(".").pop()
        const fileName = `${user.id}/${Date.now()}.${fileExt}`

        const { error: uploadError } = await supabase.storage.from("item-images").upload(fileName, imageFile)

        if (uploadError) {
          console.error("Upload error:", uploadError)
          alert("Image upload failed, but item will be updated without new photo")
        } else {
          const {
            data: { publicUrl },
          } = supabase.storage.from("item-images").getPublicUrl(fileName)
          imageUrl = publicUrl
          console.log("New image uploaded successfully:", imageUrl)
        }
      }

      // Prepare update data
      const updateData = {
        title: formData.title.trim(),
        description: formData.description.trim() || null,
        category: formData.category,
        quantity: formData.quantity.trim(),
        condition: item.item_type === "non-food" ? formData.condition || null : null,
        expiry_date: item.item_type === "food" && formData.expiry_date ? formData.expiry_date : null,
        pickup_location: formData.pickup_location.trim(),
        image_url: imageUrl,
        status: formData.status,
      }

      console.log("Updating item data:", updateData)

      // Update item
      const { data, error } = await supabase
        .from("items")
        .update(updateData)
        .eq("id", item.id)
        .eq("user_id", user.id) // Ensure user can only update their own items
        .select()

      if (error) {
        console.error("Database error:", error)
        throw error
      }

      console.log("Item updated successfully:", data)

      onItemUpdated()
      onClose()
      alert(`${item.item_type === "food" ? "Food" : "Item"} updated successfully!`)
    } catch (error: any) {
      console.error("Error updating item:", error)
      alert(`Error updating item: ${error.message}`)
    } finally {
      setLoading(false)
    }
  }

  const getCategories = () => {
    if (item?.item_type === "food") {
      return ["fruits", "vegetables", "grains", "dairy", "meat", "prepared", "other"]
    } else {
      return ["clothing", "shoes", "household", "electronics", "books", "toys", "baby-items", "other"]
    }
  }

  const categories = getCategories()
  const conditions = ["new", "excellent", "good", "fair", "needs-repair", "old"]
  const statuses = ["available", "requested", "reserved", "completed"]

  if (!item) return null

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-2xl text-green-700">
            Edit {item.item_type === "food" ? "Food" : "Item"}
          </DialogTitle>
          <DialogDescription>Update your {item.item_type === "food" ? "food" : "item"} listing</DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="edit-title">{item.item_type === "food" ? "Food" : "Item"} Title *</Label>
            <Input
              id="edit-title"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              placeholder={
                item.item_type === "food" ? "e.g., Fresh tomatoes, Cooked rice" : "e.g., Men's shoes, Baby clothes"
              }
              required
            />
          </div>

          <div>
            <Label htmlFor="edit-category">Category *</Label>
            <Select
              value={formData.category}
              onValueChange={(value) => setFormData({ ...formData, category: value })}
              required
            >
              <SelectTrigger>
                <SelectValue placeholder="Select category" />
              </SelectTrigger>
              <SelectContent>
                {categories.map((category) => (
                  <SelectItem key={category} value={category}>
                    {category.charAt(0).toUpperCase() + category.slice(1).replace("-", " ")}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label htmlFor="edit-quantity">Quantity *</Label>
            <Input
              id="edit-quantity"
              value={formData.quantity}
              onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
              placeholder={item.item_type === "food" ? "e.g., 2 kg, 5 pieces, 1 bowl" : "e.g., 1 pair, 3 pieces, 1 set"}
              required
            />
          </div>

          {item.item_type === "non-food" && (
            <div>
              <Label htmlFor="edit-condition">Condition *</Label>
              <Select
                value={formData.condition}
                onValueChange={(value) => setFormData({ ...formData, condition: value })}
                required
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select condition" />
                </SelectTrigger>
                <SelectContent>
                  {conditions.map((condition) => (
                    <SelectItem key={condition} value={condition}>
                      {condition.charAt(0).toUpperCase() + condition.slice(1).replace("-", " ")}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          )}

          <div>
            <Label htmlFor="edit-pickup-location">Pickup Location *</Label>
            <Input
              id="edit-pickup-location"
              value={formData.pickup_location}
              onChange={(e) => setFormData({ ...formData, pickup_location: e.target.value })}
              placeholder="e.g., Area 25, Lilongwe"
              required
            />
          </div>

          {item.item_type === "food" && (
            <div>
              <Label htmlFor="edit-expiry-date">Expiry Date (optional)</Label>
              <Input
                id="edit-expiry-date"
                type="date"
                value={formData.expiry_date}
                onChange={(e) => setFormData({ ...formData, expiry_date: e.target.value })}
              />
            </div>
          )}

          <div>
            <Label htmlFor="edit-status">Status</Label>
            <Select value={formData.status} onValueChange={(value) => setFormData({ ...formData, status: value })}>
              <SelectTrigger>
                <SelectValue placeholder="Select status" />
              </SelectTrigger>
              <SelectContent>
                {statuses.map((status) => (
                  <SelectItem key={status} value={status}>
                    {status.charAt(0).toUpperCase() + status.slice(1)}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label htmlFor="edit-description">Description</Label>
            <Textarea
              id="edit-description"
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              placeholder={
                item.item_type === "food"
                  ? "Additional details about the food..."
                  : "Additional details about the item..."
              }
              rows={3}
            />
          </div>

          <div>
            <Label htmlFor="edit-image">Update Photo (optional)</Label>
            <Input
              id="edit-image"
              type="file"
              accept="image/*"
              onChange={(e) => setImageFile(e.target.files?.[0] || null)}
            />
            {item.image_url && (
              <p className="text-sm text-gray-500 mt-1">Current photo will be replaced if you upload a new one</p>
            )}
          </div>

          <div className="flex gap-2 pt-4">
            <Button type="submit" className="flex-1 bg-green-600 hover:bg-green-700" disabled={loading}>
              {loading ? "Updating..." : "Update Item"}
            </Button>
            <Button type="button" variant="outline" onClick={onClose} disabled={loading}>
              Cancel
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  )
}

components/ItemList.tsx.content:
"use client"

import React, { useState, useEffect } from "react"
import { supabase } from "@/lib/supabase"
import { useAuth } from "@/contexts/AuthContext"
import { useLocation } from "@/contexts/LocationContext"
import { formatDistance, calculateDistance } from "@/lib/locationService"
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { Input } from "@/components/ui/input"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { CalendarDays, MapPin, Package, Search, Filter, Plus, Eye, Edit, Trash2, Image as ImageIcon, AlertCircle } from "lucide-react"
import ClaimFoodModal from "./ClaimFoodModal"
import EditItemModal from "./EditItemModal"

interface ItemRecord {
  id: string
  title: string
  description: string | null
  category: string
  item_type: "food" | "non-food"
  quantity: string
  condition?: string | null
  expiry_date?: string | null
  pickup_location: string
  image_url: string | null
  user_id: string
  status: string
  created_at: string
  collaboration_id?: string | null
  latitude?: number | null
  longitude?: number | null
  distance?: number
  profiles?: {
    full_name: string | null
    location: string | null
  }
}

interface ItemListProps {
  itemType: "food" | "non-food"
  collaborationId?: string | null
}

// Utility function to calculate expiry urgency
const getExpiryUrgency = (expiryDate: string) => {
  const now = new Date();
  const expiry = new Date(expiryDate);
  const hoursUntilExpiry = (expiry.getTime() - now.getTime()) / (1000 * 60 * 60);

  if (hoursUntilExpiry < 0) {
    return { level: 'expired', color: 'red', text: 'Expired' };
  } else if (hoursUntilExpiry <= 24) {
    return { level: 'urgent', color: 'red', text: 'Expires today' };
  } else if (hoursUntilExpiry <= 72) {
    return { level: 'soon', color: 'amber', text: 'Expires soon' };
  } else {
    return { level: 'fresh', color: 'green', text: 'Fresh' };
  }
};

export default function ItemList({ itemType, collaborationId }: ItemListProps) {
  const { user } = useAuth()
  const { selectedLocation, locationRadius } = useLocation()
  const [items, setItems] = useState<ItemRecord[]>([])
  const [loading, setLoading] = useState(true)
  const [selectedItem, setSelectedItem] = useState<ItemRecord | null>(null)
  const [editingItem, setEditingItem] = useState<ItemRecord | null>(null)
  const [searchTerm, setSearchTerm] = useState("")
  const [categoryFilter, setCategoryFilter] = useState("all")
  const [statusFilter, setStatusFilter] = useState("all")
  const [currentPage, setCurrentPage] = useState(0)
  const [hasMore, setHasMore] = useState(true)
  const [profiles, setProfiles] = useState<Record<string, any>>({})
  const [requestingItems, setRequestingItems] = useState<Set<string>>(new Set())
  const [requestCounts, setRequestCounts] = useState<Record<string, number>>({})

  const itemsPerPage = 12

  const categories = itemType === "food" 
    ? ["fruits", "vegetables", "meat", "dairy", "grains", "prepared", "other"]
    : ["clothing", "shoes", "household", "electronics", "books", "toys", "baby-items", "other"]

  // Helper function to format category display names
  const formatCategoryName = (category: string) => {
    if (category === "baby-items") return "Baby Items";
    if (category === "prepared") return "Prepared Food";
    return category.charAt(0).toUpperCase() + category.slice(1);
  };

  useEffect(() => {
    fetchItems(true)
  }, [itemType, collaborationId, searchTerm, categoryFilter, statusFilter, selectedLocation, locationRadius])

  const fetchItems = async (reset = false) => {
    try {
      const pageToFetch = reset ? 0 : currentPage
      console.log(`üîç Fetching ${itemType} items (page ${pageToFetch})...`)

      let query = supabase
        .from("items")
        .select("*")
        .eq("item_type", itemType)
        .order("created_at", { ascending: false })

      // Add collaboration filtering
      if (collaborationId) {
        query = query.eq("collaboration_id", collaborationId)
      } else {
        // If not filtering by collaboration, exclude collaboration items
        query = query.is("collaboration_id", null)
      }

      if (searchTerm) {
        query = query.or(`title.ilike.%${searchTerm}%,description.ilike.%${searchTerm}%`)
      }

      if (categoryFilter !== "all") {
        console.log(`üîç Filtering by category: ${categoryFilter}`)
        query = query.eq("category", categoryFilter)
      }

      if (statusFilter !== "all") {
        query = query.eq("status", statusFilter)
      }

      // Execute the query first
      const { data, error } = await query
        .range(pageToFetch * itemsPerPage, (pageToFetch + 1) * itemsPerPage - 1)

      if (error) throw error

      let filteredData = data || [];

      // Add location-based filtering if location is selected (client-side)
      if (selectedLocation && selectedLocation.latitude && selectedLocation.longitude) {
        console.log(`üîç Filtering by location: ${selectedLocation.latitude}, ${selectedLocation.longitude} (${locationRadius}km radius)`);

        filteredData = filteredData.filter(item => {
          if (!item.latitude || !item.longitude) return false;

          const distance = calculateDistance(
            selectedLocation.latitude,
            selectedLocation.longitude,
            item.latitude,
            item.longitude
          );

          // Add distance to item for display
          item.distance = distance;

          return distance <= locationRadius;
        });

        // Sort by distance
        filteredData.sort((a, b) => (a.distance || 0) - (b.distance || 0));
      }

      console.log(`‚úÖ Successfully fetched ${filteredData.length} ${itemType} items`)

      if (reset) {
        setItems(filteredData)
        setCurrentPage(0)
      } else {
        setItems(prev => [...prev, ...filteredData])
      }

      setHasMore(filteredData.length === itemsPerPage)

      // Fetch user profiles for items
      if (filteredData && filteredData.length > 0) {
        const userIds = [...new Set(filteredData.map(item => item.user_id))]
        await fetchProfiles(userIds)

        // Fetch request counts for owner's items
        if (user) {
          await fetchRequestCounts(filteredData.filter(item => item.user_id === user.id))
        }
      }
    } catch (error) {
      console.error(`‚ùå Error fetching ${itemType} items:`, error)
    } finally {
      setLoading(false)
    }
  }

  const fetchProfiles = async (userIds: string[]) => {
    try {
      console.log(`üë• Fetching profiles for ${userIds.length} users...`)

      const { data, error } = await supabase
        .from("profiles")
        .select("id, full_name, location")
        .in("id", userIds)

      if (error) throw error

      const profilesMap = (data || []).reduce((acc, profile) => {
        acc[profile.id] = profile
        return acc
      }, {} as Record<string, any>)

      setProfiles(prev => ({ ...prev, ...profilesMap }))
      console.log(`‚úÖ Successfully fetched ${data?.length || 0} profiles`)
    } catch (error) {
      console.error("‚ùå Error fetching profiles:", error)
    }
  }

  const fetchRequestCounts = async (ownerItems: ItemRecord[]) => {
    try {
      const itemIds = ownerItems.map(item => item.id)
      if (itemIds.length === 0) return

      const { data, error } = await supabase
        .from("claims")
        .select("item_id")
        .in("item_id", itemIds)
        .eq("status", "pending")

      if (error) throw error

      const counts = (data || []).reduce((acc, claim) => {
        acc[claim.item_id] = (acc[claim.item_id] || 0) + 1
        return acc
      }, {} as Record<string, number>)

      setRequestCounts(counts)
    } catch (error) {
      console.error("‚ùå Error fetching request counts:", error)
    }
  }

  const loadMore = () => {
    if (!loading && hasMore) {
      setCurrentPage(prev => prev + 1)
      fetchItems(false)
    }
  }

  const handleItemClaimed = () => {
    fetchItems(true)
    setSelectedItem(null)
  }

  const handleItemUpdated = () => {
    fetchItems(true)
    setEditingItem(null)
  }

  const handleDeleteItem = async (itemId: string) => {
    if (!user) return

    try {
      const { error } = await supabase
        .from("items")
        .delete()
        .eq("id", itemId)
        .eq("user_id", user.id)

      if (error) throw error

      fetchItems(true)
    } catch (error) {
      console.error("‚ùå Error deleting item:", error)
      alert("Failed to delete item")
    }
  }

  const filteredItems = items

  if (loading && currentPage === 0) {
    return (
      <div className="space-y-4">
        {[...Array(6)].map((_, i) => (
          <Card key={i} className="animate-pulse">
            <CardContent className="p-4">
              <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
              <div className="h-3 bg-gray-200 rounded w-1/2"></div>
            </CardContent>
          </Card>
        ))}
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {/* Search and Filters - Mobile Optimized */}
      <div className="space-y-4">
        {/* Search Bar - Full Width on Mobile */}
        <div className="relative">
          <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
          <Input
            placeholder={`Search ${itemType} items...`}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-12 pr-4 py-4 text-base mobile-text-base rounded-xl border-2 border-gray-200 focus:border-green-500 transition-colors w-full"
          />
        </div>

        {/* Filters - Horizontal Scroll on Mobile */}
        <div className="flex gap-3 overflow-x-auto pb-2 scrollbar-hide md:justify-start">
          <Select value={categoryFilter} onValueChange={setCategoryFilter}>
            <SelectTrigger className="min-w-[140px] h-12 px-4 rounded-xl border-2 border-gray-200 bg-white hover:border-green-300 transition-colors">
              <SelectValue placeholder="Category" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Categories</SelectItem>
              {categories.map(category => (
                <SelectItem key={category} value={category}>{formatCategoryName(category)}</SelectItem>
              ))}
            </SelectContent>
          </Select>

          <Select value={statusFilter} onValueChange={setStatusFilter}>
            <SelectTrigger className="min-w-[120px] h-12 px-4 rounded-xl border-2 border-gray-200 bg-white hover:border-green-300 transition-colors">
              <SelectValue placeholder="Status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Status</SelectItem>
              <SelectItem value="available">Available</SelectItem>
              <SelectItem value="requested">Requested</SelectItem>
              <SelectItem value="reserved">Reserved</SelectItem>
              <SelectItem value="completed">Completed</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Items Grid */}
      {filteredItems.length === 0 ? (
        <Card>
          <CardContent className="p-8 text-center">
            <Package className="h-12 w-12 mx-auto mb-4 text-gray-400" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              No {itemType} items found
            </h3>
            <p className="text-gray-600">
              {collaborationId 
                ? "No items have been shared with this collaboration yet."
                : `No ${itemType} items match your current filters.`
              }
            </p>
          </CardContent>
        </Card>
      ) : (
        <>
          {/* Mobile: Vertical Feed Layout */}
          <div className="lg:hidden space-y-3">
            {filteredItems.map((item) => {
              // Precompute urgency to avoid IIFE in JSX
              const urgency = item.expiry_date ? getExpiryUrgency(item.expiry_date) : null;

              return (
              <Card key={item.id} className="group overflow-hidden hover:shadow-xl transition-all duration-300 cursor-pointer border border-gray-200 hover:border-green-300 bg-white rounded-3xl relative">
                <div className="flex h-24">
                  {/* Image Section - Fixed 4:3 aspect ratio */}
                  <div className="relative w-32 h-24 flex-shrink-0 overflow-hidden rounded-l-3xl bg-gradient-to-br from-gray-50 to-gray-100">
                    {item.image_url ? (
                      <img
                        src={item.image_url}
                        alt={item.title}
                        className="object-cover object-center w-full h-full group-hover:scale-110 transition-transform duration-300"
                        loading="lazy"
                        onError={(e) => {
                          const target = e.target as HTMLImageElement;
                          target.style.display = 'none';
                          target.parentElement!.innerHTML = '<div class="flex items-center justify-center h-full text-gray-400 text-xs font-medium">No image</div>';
                        }}
                      />
                    ) : (
                      <div className="flex items-center justify-center h-full text-gray-400">
                        <div className="text-center">
                          <ImageIcon className="h-8 w-8 mx-auto mb-1 text-gray-300" />
                          <span className="text-xs font-medium">No image</span>
                        </div>
                      </div>
                    )}

                    {/* Status Indicator - More prominent */}
                    <div className="absolute top-2 left-2">
                      <div className={`w-4 h-4 rounded-full border-2 border-white shadow-lg ${
                        item.status === 'available' ? 'bg-green-500' :
                        item.status === 'requested' ? 'bg-amber-500' :
                        item.status === 'reserved' ? 'bg-blue-500' :
                        'bg-gray-500'
                      }`}></div>
                    </div>

                    {/* Distance Badge - More prominent */}
                    {item.distance !== undefined && (
                      <div className="absolute top-2 right-2">
                        <div className="bg-blue-600 text-white text-sm px-3 py-1 rounded-full font-bold shadow-lg border-2 border-white">
                          {formatDistance(item.distance)}
                        </div>
                      </div>
                    )}
                  </div>

                  {/* Content Section */}
                  <div className="flex-1 p-4 pb-14 pr-24 flex flex-col justify-between">
                    {/* Top Section - Title & Category */}
                    <div className="space-y-2">
                      <div className="flex items-start justify-between gap-2">
                        <div className="flex-1">
                          <h3 className="font-bold text-lg text-gray-900 line-clamp-2 leading-tight group-hover:text-green-700 transition-colors">
                            {item.title}
                          </h3>
                        </div>
                      </div>

                      <div className="flex items-center gap-2">
                        <Badge variant="secondary" className="bg-green-100 text-green-800 text-xs font-medium">
                          {item.category}
                        </Badge>
                        <Badge 
                          variant={item.status === 'available' ? 'default' : 'secondary'}
                          className={`text-xs font-medium ${
                            item.status === 'available' ? 'bg-green-500 text-white' :
                            item.status === 'requested' ? 'bg-amber-100 text-amber-800' :
                            item.status === 'reserved' ? 'bg-blue-100 text-blue-800' :
                            'bg-gray-100 text-gray-800'
                          }`}
                        >
                          {item.status}
                        </Badge>
                        {/* Separate requests indicator */}
                        {item.status === 'available' && requestCounts[item.id] > 0 && (
                          <Badge className="text-xs font-bold bg-amber-100 text-amber-800 animate-pulse">
                            {requestCounts[item.id]} requests
                          </Badge>
                        )}
                      </div>

                      {/* Key Info Row with Urgency */}
                      <div className="flex items-center gap-2 flex-wrap">
                        {/* Quantity */}
                        <div className="flex items-center gap-1 bg-gray-100 px-2 py-1 rounded-full">
                          <Package className="h-3 w-3 text-gray-600" />
                          <span className="text-xs font-medium text-gray-700">{item.quantity}</span>
                        </div>

                        {/* Condition for non-food items */}
                        {item.condition && (
                          <Badge variant="outline" className="text-xs px-2 py-1 bg-blue-50 text-blue-700 border-blue-200">
                            {item.condition}
                          </Badge>
                        )}

                        {/* Expiry with urgency colors for food items */}
                        {urgency && (
                          <div className={`flex items-center gap-1 px-2 py-1 rounded-full text-xs font-bold ${
                            urgency.color === 'red' ? 'bg-red-100 text-red-700 border border-red-200' :
                            urgency.color === 'amber' ? 'bg-amber-100 text-amber-700 border border-amber-200' :
                            'bg-green-100 text-green-700 border border-green-200'
                          }`}>
                            {urgency.color === 'red' && <AlertCircle className="h-3 w-3" />}
                            {urgency.color === 'amber' && <CalendarDays className="h-3 w-3" />}
                            {urgency.color === 'green' && <CalendarDays className="h-3 w-3" />}
                            <span>{urgency.text}</span>
                          </div>
                        )}
                      </div>
                    </div>

                    {/* Bottom Section - Location & Owner */}
                    <div className="space-y-1 mt-2">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-1 text-xs text-gray-600">
                          <MapPin className="h-3 w-3 shrink-0" />
                          <span className="truncate font-medium">{item.pickup_location}</span>
                        </div>
                        <span className="text-xs text-gray-500">
                          {new Date(item.created_at).toLocaleDateString()}
                        </span>
                      </div>
                      <p className="text-xs text-gray-500">
                        By {profiles[item.user_id]?.full_name || 'Unknown'}
                      </p>
                    </div>
                  </div>

                  {/* Single Primary CTA */}
                  <div className="absolute bottom-3 right-3">
                    {user && user.id !== item.user_id && item.status === 'available' ? (
                      <Button 
                        onClick={(e) => {
                          e.preventDefault();
                          e.stopPropagation();
                          setRequestingItems(prev => new Set(prev).add(item.id));
                          setSelectedItem(item);

                          setTimeout(() => {
                            setRequestingItems(prev => {
                              const newSet = new Set(prev);
                              newSet.delete(item.id);
                              return newSet;
                            });
                          }, 2000);
                        }}
                        className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded-full min-h-[40px] shadow-lg hover:shadow-xl transition-all duration-200 border-2 border-white"
                        disabled={requestingItems.has(item.id)}
                      >
                        {requestingItems.has(item.id) ? "..." : "Claim"}
                      </Button>
                    ) : user && user.id === item.user_id ? (
                      <div className="flex gap-2">
                        <Button 
                          onClick={(e) => {
                            e.preventDefault();
                            e.stopPropagation();
                            setEditingItem(item);
                          }}
                          variant="outline"
                          className="bg-white hover:bg-gray-50 border-gray-300 hover:border-green-500 text-gray-700 hover:text-green-700 font-medium py-2 px-3 rounded-full shadow-lg"
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button 
                          onClick={(e) => {
                            e.preventDefault();
                            e.stopPropagation();
                            if (confirm('Are you sure you want to delete this item?')) {
                              handleDeleteItem(item.id)
                            }
                          }}
                          variant="outline"
                          className="bg-white hover:bg-red-50 border-gray-300 hover:border-red-500 text-gray-700 hover:text-red-700 font-medium py-2 px-3 rounded-full shadow-lg"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    ) : null}
                  </div>
                </div>
              </Card>
              );
            })}
          </div>

          {/* Desktop: Grid Layout */}
          <div className="hidden lg:grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4 sm:gap-6">
            {filteredItems.map((item) => (
              <Card key={item.id} className="group overflow-hidden hover:shadow-lg hover:scale-105 transition-all duration-300 cursor-pointer border-2 hover:border-green-200 w-full max-w-[308px] mx-auto relative">
                {/* Visual Enhancement: Gradient Overlay */}
                <div className="absolute inset-0 bg-gradient-to-br from-green-500/10 via-transparent to-blue-500/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none z-10"></div>

                {/* Visual Enhancement: Corner Accent */}
                <div className="absolute top-0 right-0 w-0 h-0 border-l-[20px] border-l-transparent border-t-[20px] border-t-green-400 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>

                <div className="relative h-40 w-full overflow-hidden rounded-t-lg bg-gradient-to-br from-gray-50 to-gray-100">
                  {item.image_url ? (
                    <img
                      src={item.image_url}
                      alt={item.title}
                      className="object-cover object-center w-full h-full group-hover:scale-110 transition-transform duration-300"
                      onError={(e) => {
                        const target = e.target as HTMLImageElement;
                        target.style.display = 'none';
                        target.parentElement!.innerHTML = '<div class="flex items-center justify-center h-full text-gray-400"><span>Image not available</span></div>';
                      }}
                    />
                  ) : (
                    <div className="flex items-center justify-center h-full text-gray-400">
                      <div className="text-center">
                        <ImageIcon className="h-8 w-8 mx-auto mb-1 text-gray-300" />
                        <span className="text-xs">No image</span>
                      </div>
                    </div>
                  )}

                  {/* Visual Enhancement: Status Indicator */}
                  <div className="absolute top-2 left-2">
                    <div className={`w-3 h-3 rounded-full border-2 border-white shadow-sm ${
                      item.status === 'available' ? 'bg-green-500' :
                      item.status === 'requested' ? 'bg-yellow-500' :
                      item.status === 'reserved' ? 'bg-blue-500' :
                      'bg-gray-500'
                    }`}></div>
                  </div>

                  {/* Visual Enhancement: Distance Badge */}
                  {item.distance !== undefined && (
                    <div className="absolute top-2 right-2">
                      <div className="bg-white/90 backdrop-blur-sm text-blue-700 text-xs px-2 py-1 rounded-full font-medium shadow-sm">
                        {formatDistance(item.distance)}
                      </div>
                    </div>
                  )}
                </div>
                <CardHeader className="p-3 pb-2">
                  <div className="flex justify-between items-start">
                    <div className="flex-1 relative group">
                      <CardTitle className="text-sm font-semibold flex items-center gap-1 line-clamp-2 group-hover:line-clamp-none">
                        {item.title}
                        {requestCounts[item.id] > 0 && (
                          <span className="ml-1 inline-flex items-center justify-center px-1 py-0.5 rounded-full text-xs font-semibold bg-yellow-200 text-yellow-800 animate-pulse">
                            {requestCounts[item.id]}
                          </span>
                        )}
                      </CardTitle>
                      <div className="absolute hidden group-hover:block bg-black text-white text-xs p-2 rounded shadow-lg z-10 mt-1 whitespace-normal max-w-xs">
                        {item.title}
                      </div>
                    </div>
                    <div className="flex flex-col gap-1 shrink-0">
                      <Badge variant="secondary" className="bg-green-100 text-green-800 text-xs">
                        {item.category}
                      </Badge>
                    <Badge 
                      variant={item.status === 'available' ? 'default' : 'secondary'}
                        className={`text-xs ${
                        item.status === 'available' ? 'bg-green-100 text-green-800' :
                        item.status === 'requested' ? 'bg-yellow-100 text-yellow-800' :
                        item.status === 'reserved' ? 'bg-blue-100 text-blue-800' :
                        'bg-gray-100 text-gray-800'
                      }`}
                    >
                      {item.status === 'available' && requestCounts[item.id] > 0 
                        ? `Requested (${requestCounts[item.id]})` 
                        : item.status}
                    </Badge>
                  </div>
                  </div>
                  <div className="relative group">
                    <CardDescription className="line-clamp-2 text-xs leading-tight group-hover:line-clamp-none">
                        {item.description}
                    </CardDescription>
                    <div className="absolute hidden group-hover:block bg-black text-white text-xs p-2 rounded shadow-lg z-10 mt-1 whitespace-normal max-w-xs">
                        {item.description}
                      </div>
                    </div>
                </CardHeader>
                <CardContent className="space-y-2 p-3 pt-0">
                  <div className="flex items-center gap-1 text-xs text-gray-600 relative group">
                    <MapPin className="h-4 w-4 shrink-0" />
                    <span className="truncate group-hover:overflow-visible">{item.pickup_location}</span>
                    <div className="absolute hidden group-hover:block bg-black text-white text-xs p-2 rounded shadow-lg z-10 mt-6 whitespace-nowrap">
                      {item.pickup_location}
                    </div>
                  </div>

                  <div className="flex justify-between items-center text-xs">
                    <div className="flex items-center gap-1">
                      <Package className="h-4 w-4" />
                      <span className="font-medium">{item.quantity}</span>
                    </div>
                    {item.condition && (
                      <Badge variant="outline" className="text-xs">
                        {item.condition}
                      </Badge>
                    )}
                  </div>

                  {item.expiry_date && (
                    <div className="flex items-center gap-1 text-orange-600 text-xs">
                      <CalendarDays className="h-4 w-4" />
                      <span>Expires: {new Date(item.expiry_date).toLocaleDateString()}</span>
                    </div>
                  )}

                  <div className="text-xs text-gray-500">
                    <span className="block">
                      By {profiles[item.user_id]?.full_name || 'Unknown'} ‚Ä¢ {new Date(item.created_at).toLocaleDateString()}
                    </span>
                  </div>

                  <div className="flex gap-1 pt-2">
                    {user && user.id !== item.user_id && item.status === 'available' && (
                      <Button 
                        onClick={(e) => {
                          e.preventDefault();
                          e.stopPropagation();
                          setRequestingItems(prev => new Set(prev).add(item.id));
                          setSelectedItem(item);

                          // Remove loading state after a delay
                          setTimeout(() => {
                            setRequestingItems(prev => {
                              const newSet = new Set(prev);
                              newSet.delete(item.id);
                              return newSet;
                            });
                          }, 2000);
                        }}
                        className="flex-1 text-xs py-2 h-auto bg-green-600 hover:bg-green-700 text-white disabled:opacity-50 transform hover:scale-105 transition-all duration-200"
                        size="sm"
                        disabled={requestingItems.has(item.id)}
                      >
                        <Plus className="h-4 w-4 mr-1" />
                        {requestingItems.has(item.id) ? "Requesting..." : "Request"}
                      </Button>
                    )}

                    {user && user.id === item.user_id && (
                      <>
                        <Button 
                          onClick={() => setEditingItem(item)}
                          variant="outline"
                          size="sm"
                          className="flex-1 text-xs py-2 h-auto hover:scale-105 transition-all duration-200"
                        >
                          <Edit className="h-4 w-4 mr-1" />
                          Edit
                        </Button>
                        <Button 
                          onClick={() => {
                            if (confirm('Are you sure you want to delete this item?')) {
                              handleDeleteItem(item.id)
                            }
                          }}
                          variant="outline"
                          size="sm"
                          className="text-red-600 hover:text-red-700 text-xs py-2 h-auto px-3 hover:scale-105 transition-all duration-200"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {hasMore && (
            <div className="text-center">
              <Button 
                onClick={loadMore}
                variant="outline"
                disabled={loading}
              >
                {loading ? "Loading..." : "Load More"}
              </Button>
            </div>
          )}
        </>
      )}

      {/* Modals */}
      {selectedItem && (
        <ClaimFoodModal
          foodItem={{
            ...selectedItem,
            owner_name: profiles[selectedItem.user_id]?.full_name
          }}
          isOpen={!!selectedItem}
          onClose={() => setSelectedItem(null)}
          onClaimed={handleItemClaimed}
        />
      )}

      {editingItem && (
        <EditItemModal
          item={{
            ...editingItem,
            expiry_date: editingItem.expiry_date || null
          }}
          isOpen={!!editingItem}
          onClose={() => setEditingItem(null)}
          onItemUpdated={handleItemUpdated}
        />
      )}
    </div>
  )
}

