"use client"

import React, { useState } from 'react';
import { useLocation } from '@/contexts/LocationContext';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Badge } from '@/components/ui/badge';
import { MapPin, Navigation, Globe, Settings, RefreshCw, ChevronDown } from 'lucide-react';
import { formatDistance, RADIUS_OPTIONS, getCityCoordinates } from '@/lib/locationService';

interface LocationSelectorProps {
  showRadiusSelector?: boolean;
  className?: string;
}

export default function LocationSelector({ showRadiusSelector = true, className = '' }: LocationSelectorProps) {
  const {
    selectedLocation,
    locationRadius,
    isLoadingLocation,
    locationError,
    setSelectedLocation,
    setLocationRadius,
    getCurrentUserLocation,
    clearLocationError,
    availableCities,
  } = useLocation();

  const [isRefreshing, setIsRefreshing] = useState(false);
  const [isOpen, setIsOpen] = useState(false);

  const handleLocationRefresh = async () => {
    setIsRefreshing(true);
    await getCurrentUserLocation();
    setIsRefreshing(false);
  };

  const handleCityChange = (cityName: string) => {
    const cityLocation = getCityCoordinates(cityName);
    if (cityLocation) {
      setSelectedLocation(cityLocation);
    }
  };

  const handleRadiusChange = (radius: string) => {
    setLocationRadius(parseInt(radius));
  };

  const getLocationDisplay = () => {
    if (!selectedLocation) return 'Select location';

    if (selectedLocation.city) {
      return selectedLocation.city;
    }

    if (selectedLocation.address) {
      return selectedLocation.address.split(',')[0]; // Show first part of address
    }

    return `${selectedLocation.latitude.toFixed(4)}, ${selectedLocation.longitude.toFixed(4)}`;
  };

  const getCompactDisplay = () => {
    if (!selectedLocation) return 'Set Location';

    if (selectedLocation.city) {
      return `${selectedLocation.city} (${formatDistance(locationRadius)})`;
    }

    if (selectedLocation.address) {
      const shortAddress = selectedLocation.address.split(',')[0];
      return `${shortAddress} (${formatDistance(locationRadius)})`;
    }

    return `${formatDistance(locationRadius)} radius`;
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button 
          variant="outline" 
          className={`flex items-center gap-3 px-4 py-3 min-h-[48px] rounded-xl border-2 border-gray-200 hover:border-green-500 transition-colors touch-target-lg ${className}`}
        >
          <MapPin className="h-5 w-5 text-blue-600" />
          <span className="text-sm font-medium mobile-text-base">{getCompactDisplay()}</span>
          <ChevronDown className="h-4 w-4 text-gray-500" />
        </Button>
      </DialogTrigger>

      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <MapPin className="h-5 w-5 text-blue-600" />
            Location & Radius Settings
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Current Location Display */}
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Navigation className="h-4 w-4 text-green-600" />
              <span className="text-sm font-medium">Current Location:</span>
            </div>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleLocationRefresh}
              disabled={isRefreshing || isLoadingLocation}
              className="h-8 px-2"
            >
              <RefreshCw className={`h-4 w-4 ${isRefreshing ? 'animate-spin' : ''}`} />
            </Button>
          </div>

          <div className="bg-gray-50 rounded-lg p-3">
            <div className="flex items-center gap-2 mb-2">
              <Globe className="h-4 w-4 text-blue-600" />
              <span className="font-medium text-sm">{getLocationDisplay()}</span>
            </div>

            {selectedLocation && (
              <div className="text-xs text-gray-600">
                {selectedLocation.latitude.toFixed(6)}, {selectedLocation.longitude.toFixed(6)}
              </div>
            )}
          </div>

          {/* Location Error */}
          {locationError && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-3">
              <div className="flex items-center gap-2 text-red-700">
                <Settings className="h-4 w-4" />
                <span className="text-sm">{locationError}</span>
              </div>
              <Button
                variant="ghost"
                size="sm"
                onClick={clearLocationError}
                className="mt-2 h-6 px-2 text-xs"
              >
                Dismiss
              </Button>
            </div>
          )}

          {/* City Selector */}
          <div className="space-y-2">
            <label className="text-sm font-medium">Select City:</label>
            <Select
              value={selectedLocation?.city || ''}
              onValueChange={handleCityChange}
            >
              <SelectTrigger className="h-12 px-4 rounded-xl border-2 border-gray-200 hover:border-green-500 transition-colors touch-target-lg">
                <SelectValue placeholder="Choose a city" />
              </SelectTrigger>
              <SelectContent>
                {availableCities.map((city) => (
                  <SelectItem key={city} value={city}>
                    {city}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Radius Selector */}
          {showRadiusSelector && (
            <div className="space-y-2">
              <label className="text-sm font-medium">Search Radius:</label>
              <Select
                value={locationRadius.toString()}
                onValueChange={handleRadiusChange}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {RADIUS_OPTIONS.map((option) => (
                    <SelectItem key={option.value} value={option.value.toString()}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <div className="flex items-center gap-2 text-xs text-gray-600">
                <MapPin className="h-3 w-3" />
                <span>Items within {formatDistance(locationRadius)} of your location</span>
              </div>
            </div>
          )}

          {/* Loading State */}
          {(isLoadingLocation || isRefreshing) && (
            <div className="flex items-center gap-2 text-sm text-blue-600">
              <RefreshCw className="h-4 w-4 animate-spin" />
              <span>Getting your location...</span>
            </div>
          )}

          {/* Close Button */}
          <div className="flex justify-end pt-2">
            <Button onClick={() => setIsOpen(false)} className="bg-blue-600 hover:bg-blue-700">
              Done
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

components/MyItemsManager.tsx.content:
"use client"

import { useEffect, useState, useCallback, useMemo } from "react"
import { supabase } from "@/lib/supabase"
import { useAuth } from "@/contexts/AuthContext"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { MapPin, Clock, Package, Edit, Trash2, CheckCircle } from "lucide-react"
import Image from "next/image"
import EditItemModal from "@/components/EditItemModal"

interface Item {
  id: string
  title: string
  description: string | null
  category: string
  item_type: "food" | "non-food"
  quantity: string
  condition?: string | null
  expiry_date: string | null
  pickup_location: string
  image_url: string | null
  status: string
  created_at: string
  user_id: string
}

interface MyItemsManagerProps {
  onItemUpdated: () => void
}

export default function MyItemsManager({ onItemUpdated }: MyItemsManagerProps) {
  const { user } = useAuth()
  const [items, setItems] = useState<Item[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [editingItem, setEditingItem] = useState<Item | null>(null)
  const [showEditModal, setShowEditModal] = useState(false)
  const [filter, setFilter] = useState("all")
  const [deleting, setDeleting] = useState<string | null>(null)
  const [requestCounts, setRequestCounts] = useState<{ [itemId: string]: number }>({})
  const [markingShared, setMarkingShared] = useState<string | null>(null)

  // Fetch request counts for each item
  const fetchRequestCounts = useCallback(async (itemIds: string[]) => {
    if (itemIds.length === 0) return setRequestCounts({})
    const { data, error } = await supabase
      .from("claims")
      .select("item_id, status")
      .in("item_id", itemIds)
      .in("status", ["pending", "requested"])
    if (error) {
      setRequestCounts({})
      return
    }
    const counts: { [itemId: string]: number } = {}
    data.forEach((claim: { item_id: string }) => {
      counts[claim.item_id] = (counts[claim.item_id] || 0) + 1
    })
    setRequestCounts(counts)
  }, [])

  // Optimized fetch using database function
  const fetchMyItems = useCallback(async () => {
    if (!user) return

    try {
      setError(null)
      console.log("Fetching user's items...")

      const { data: itemData, error: itemError } = await supabase.rpc("get_user_items", {
        p_user_id: user.id,
        p_limit: 100,
        p_offset: 0,
      })

      if (itemError) {
        console.error("Error fetching user items:", itemError)
        setError(`Error fetching items: ${itemError.message}`)
        return
      }

      console.log("Successfully fetched user items:", itemData?.length || 0)
      setItems(itemData || [])
      // Fetch request counts for these items
      if (itemData && itemData.length > 0) {
        fetchRequestCounts(itemData.map((item: Item) => item.id))
      } else {
        setRequestCounts({})
      }
    } catch (error: any) {
      console.error("Unexpected error fetching user items:", error)
      setError(`Unexpected error: ${error.message}`)
    } finally {
      setLoading(false)
    }
  }, [user, fetchRequestCounts])

  useEffect(() => {
    if (user) {
      fetchMyItems()
    }
  }, [user, fetchMyItems])

  const handleEditItem = useCallback((item: Item) => {
    setEditingItem(item)
    setShowEditModal(true)
  }, [])

  const handleItemUpdated = useCallback(() => {
    fetchMyItems()
    onItemUpdated()
    setShowEditModal(false)
    setEditingItem(null)
  }, [fetchMyItems, onItemUpdated])

  const handleDeleteItem = useCallback(
    async (item: Item) => {
      if (!confirm(`Are you sure you want to delete "${item.title}"?`)) {
        return
      }

      setDeleting(item.id)
      try {
        const { error } = await supabase.from("items").delete().eq("id", item.id).eq("user_id", user?.id)

        if (error) {
          console.error("Error deleting item:", error)
          alert(`Error deleting item: ${error.message}`)
          return
        }

        // Remove item from local state immediately
        setItems((prev) => prev.filter((i) => i.id !== item.id))
        onItemUpdated()
      } catch (error: any) {
        console.error("Unexpected error deleting item:", error)
        alert(`Error deleting item: ${error.message}`)
      } finally {
        setDeleting(null)
      }
    },
    [user?.id, onItemUpdated],
  )

  const handleMarkAsShared = useCallback(async (item: Item) => {
    if (!confirm("Mark this item as shared? This will complete the sharing and remove it from active listings.")) return
    setMarkingShared(item.id)
    try {
      await supabase.from("items").update({ status: "completed" }).eq("id", item.id)
      fetchMyItems()
      onItemUpdated()
    } catch (error) {
      alert("Error marking as shared.")
    } finally {
      setMarkingShared(null)
    }
  }, [fetchMyItems, onItemUpdated])

  const getStatusBadge = useCallback((status: string) => {
    switch (status) {
      case "available":
        return <Badge className="bg-green-100 text-green-800">Available</Badge>
      case "requested":
        return <Badge className="bg-yellow-100 text-yellow-800">Requested</Badge>
      case "reserved":
        return <Badge className="bg-blue-100 text-blue-800">Reserved</Badge>
      case "completed":
        return <Badge className="bg-gray-100 text-gray-800">Completed</Badge>
      default:
        return <Badge variant="secondary">{status}</Badge>
    }
  }, [])

  // Memoize filtered items
  const filteredItems = useMemo(() => {
    if (filter === "requested") {
      return items.filter(
        (item) =>
          item.status === "requested" ||
          (item.status === "available" && requestCounts[item.id] > 0)
      )
    }
    return filter === "all" ? items : items.filter((item) => item.status === filter)
  }, [items, filter, requestCounts])

  // Memoize stats
  const stats = useMemo(() => {
    const requestedCount = items.filter(
      (item) =>
        item.status === "requested" ||
        (item.status === "available" && requestCounts[item.id] > 0)
    ).length;
    return {
      total: items.length,
      available: items.filter((i) => i.status === "available" && (!requestCounts[i.id] || requestCounts[i.id] === 0)).length,
      requested: requestedCount,
      completed: items.filter((i) => i.status === "completed").length,
    };
  }, [items, requestCounts])

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-base sm:text-lg lg:text-xl">Loading your items...</div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="flex flex-col justify-center items-center h-64 space-y-4">
        <div className="text-red-600 text-base sm:text-lg lg:text-xl">Error loading your items</div>
        <div className="text-red-500 text-sm sm:text-base max-w-md text-center">{error}</div>
        <Button onClick={fetchMyItems} variant="outline">
          Try Again
        </Button>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <div className="text-center">
        <h2 className="text-xl sm:text-2xl lg:text-3xl font-bold text-gray-900 mb-2">My Shared Items</h2>
        <p className="text-sm sm:text-base lg:text-lg text-gray-600">Manage your food and item listings</p>

        {/* Stats */}
        <div className="flex justify-center gap-4 mt-4">
          <div className="text-center">
            <div className="text-xl sm:text-2xl lg:text-3xl font-bold text-green-600">{stats.total}</div>
            <div className="text-xs sm:text-sm text-gray-500">Total</div>
          </div>
          <div className="text-center">
            <div className="text-xl sm:text-2xl lg:text-3xl font-bold text-blue-600">{stats.available}</div>
            <div className="text-xs sm:text-sm text-gray-500">Available</div>
          </div>
          <div className="text-center">
            <div className="text-xl sm:text-2xl lg:text-3xl font-bold text-yellow-600">{stats.requested}</div>
            <div className="text-xs sm:text-sm text-gray-500">Requested</div>
          </div>
          <div className="text-center">
            <div className="text-xl sm:text-2xl lg:text-3xl font-bold text-gray-600">{stats.completed}</div>
            <div className="text-xs sm:text-sm text-gray-500">Completed</div>
          </div>
        </div>
      </div>

      <div className="flex flex-wrap gap-2">
        {["all", "available", "requested", "reserved", "completed"].map((status) => (
          <Button
            key={status}
            variant={filter === status ? "default" : "outline"}
            size="sm"
            onClick={() => setFilter(status)}
            className={`text-xs sm:text-sm lg:text-base ${filter === status ? "bg-green-600 hover:bg-green-700" : ""}`}
          >
            {status.charAt(0).toUpperCase() + status.slice(1)}
          </Button>
        ))}
      </div>

      {filteredItems.length === 0 ? (
        <div className="text-center py-12">
          <div className="text-gray-500 text-base sm:text-lg lg:text-xl">
            {filter === "all" ? "You haven't shared any items yet" : `No ${filter} items found`}
          </div>
          <div className="text-gray-400 text-sm sm:text-base mt-2">
            {filter === "all" ? "Start sharing to help your community!" : "Try a different filter"}
          </div>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
          {filteredItems.map((item) => (
            <Card 
              key={item.id} 
              className="group overflow-hidden hover:shadow-lg hover:scale-105 transition-all duration-300 cursor-pointer border-2 hover:border-green-200 w-full max-w-[280px] mx-auto" 
              onClick={() => handleEditItem(item)}
            >
              {item.image_url && (
                <div className="relative h-40 w-full overflow-hidden rounded-t-lg">
                  <Image
                    src={item.image_url || "/placeholder.svg"}
                    alt={item.title}
                    fill
                    className="object-cover group-hover:scale-110 transition-transform duration-300"
                    loading="lazy"
                    sizes="(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw"
                  />
                </div>
              )}
              <CardHeader className="p-3 pb-2">
                <div className="flex justify-between items-start">
                  <div className="flex-1 relative group">
                    <CardTitle className="text-sm font-semibold flex items-center gap-1 line-clamp-2 group-hover:line-clamp-none">
                      {item.title}
                      {requestCounts[item.id] > 0 && (
                        <span className="ml-1 inline-flex items-center justify-center px-1 py-0.5 rounded-full text-xs font-semibold bg-yellow-200 text-yellow-800 animate-pulse">
                          {requestCounts[item.id]}
                        </span>
                      )}
                    </CardTitle>
                    <div className="absolute hidden group-hover:block bg-black text-white text-xs p-2 rounded shadow-lg z-10 mt-1 whitespace-normal max-w-xs">
                      {item.title}
                    </div>
                  </div>
                  <div className="flex flex-col gap-1 shrink-0">
                    <Badge variant="secondary" className="bg-green-100 text-green-800 text-xs">
                      {item.category}
                    </Badge>
                    {getStatusBadge(item.status)}
                  </div>
                </div>
                <div className="relative group">
                  <CardDescription className="line-clamp-2 text-xs leading-tight group-hover:line-clamp-none">
                    {item.description}
                  </CardDescription>
                  <div className="absolute hidden group-hover:block bg-black text-white text-xs p-2 rounded shadow-lg z-10 mt-1 whitespace-normal max-w-xs">
                    {item.description}
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-2 p-3 pt-0">
                <div className="flex items-center gap-1 text-xs text-gray-600 relative group">
                  <MapPin className="h-4 w-4 shrink-0" />
                  <span className="truncate group-hover:overflow-visible">{item.pickup_location}</span>
                  <div className="absolute hidden group-hover:block bg-black text-white text-xs p-2 rounded shadow-lg z-10 mt-6 whitespace-nowrap">
                    {item.pickup_location}
                  </div>
                </div>

                <div className="flex justify-between items-center text-xs">
                  <div className="flex items-center gap-1">
                    <Package className="h-4 w-4" />
                    <span className="font-medium">{item.quantity}</span>
                  </div>
                  {item.condition && (
                    <Badge variant="outline" className="text-xs">
                      {item.condition}
                    </Badge>
                  )}
                </div>

                {item.expiry_date && (
                  <div className="flex items-center gap-1 text-orange-600 text-xs">
                    <Clock className="h-4 w-4" />
                    <span>Expires: {new Date(item.expiry_date).toLocaleDateString()}</span>
                  </div>
                )}

                <div className="flex gap-1 pt-2">
                  <Button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleEditItem(item);
                    }}
                    variant="outline"
                    size="sm"
                    className="flex-1 flex items-center gap-1 text-xs py-2 h-auto hover:scale-105 transition-all duration-200"
                  >
                    <Edit className="h-3 w-3" />
                    Edit
                  </Button>
                  <Button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDeleteItem(item);
                    }}
                    variant="outline"
                    size="sm"
                    className="flex items-center gap-1 text-red-600 hover:text-red-700 hover:bg-red-50 text-xs py-2 h-auto px-2 hover:scale-105 transition-all duration-200"
                    disabled={deleting === item.id}
                  >
                    <Trash2 className="h-3 w-3" />
                    {deleting === item.id ? "..." : "Delete"}
                  </Button>
                  {item.status !== "completed" && (
                    <Button
                      onClick={e => { e.stopPropagation(); handleMarkAsShared(item); }}
                      variant="outline"
                      size="sm"
                      className="flex items-center gap-1 text-green-700 border-green-400 hover:bg-green-50 text-xs py-2 h-auto px-2 hover:scale-105 transition-all duration-200"
                      disabled={markingShared === item.id}
                    >
                      <CheckCircle className="h-3 w-3" />
                      {markingShared === item.id ? "..." : "Shared"}
                    </Button>
                  )}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      <EditItemModal
        item={editingItem}
        isOpen={showEditModal}
        onClose={() => setShowEditModal(false)}
        onItemUpdated={handleItemUpdated}
      />
    </div>
  )
}

components/NotificationCenter.tsx.content:
"use client"

import { useState, useEffect, useRef } from "react"
import { supabase } from "@/lib/supabase"
import { useAuth } from "@/contexts/AuthContext"
import { Button } from "@/components/ui/button"
import { Card, CardContent } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { ScrollArea } from "@/components/ui/scroll-area"
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog"
import { Bell, MessageCircle, Package, Users, AlertCircle } from "lucide-react"

interface Notification {
  id: string
  type: string
  title: string
  message: string
  is_read: boolean
  created_at: string
  related_item_id?: string
  related_claim_id?: string
}

interface NotificationCenterProps {
  onOpenChat: (claimId: string, otherUserName: string) => void
  onNavigateToMyItems?: () => void
}

export default function NotificationCenter({ onOpenChat, onNavigateToMyItems }: NotificationCenterProps) {
  const { user } = useAuth()
  const [notifications, setNotifications] = useState<Notification[]>([])
  const [unreadCount, setUnreadCount] = useState(0)
  const [isOpen, setIsOpen] = useState(false)
  const [isTableMissing, setIsTableMissing] = useState(false)
  const [loading, setLoading] = useState(false)
  const subscriptionRef = useRef<any>(null)
  const channelRef = useRef<string>("")

  useEffect(() => {
    if (user) {
      fetchNotifications()
    }

    // Cleanup subscription when component unmounts or user changes
    return () => {
      if (subscriptionRef.current) {
        console.log("üßπ Cleaning up notification subscription")
        subscriptionRef.current.unsubscribe()
        subscriptionRef.current = null
      }
    }
  }, [user])

  // Also fetch notifications when dialog opens
  useEffect(() => {
    if (isOpen && user) {
      fetchNotifications()
    }
  }, [isOpen, user])

  const fetchNotifications = async () => {
    if (!user) return

    try {
      setLoading(true)
      console.log("üì° Fetching notifications for user:", user.id)

      const { data, error } = await supabase
        .from("notifications")
        .select("*")
        .eq("user_id", user.id)
        .order("created_at", { ascending: false })
        .limit(20)

      if (error) {
        console.error("‚ùå Error fetching notifications:", error)
        // Check if the error is due to missing table
        if (error.message.includes('relation "public.notifications" does not exist')) {
          setIsTableMissing(true)
          return
        }
      } else {
        console.log("‚úÖ Notifications fetched:", data?.length || 0)
        setNotifications(data || [])
        setUnreadCount(data?.filter((n) => !n.is_read).length || 0)
        setIsTableMissing(false)

        // Set up real-time subscription only if we don't have one already
        setupRealtimeSubscription()
      }
    } catch (error) {
      console.error("üí• Unexpected error fetching notifications:", error)
      setIsTableMissing(true)
    } finally {
      setLoading(false)
    }
  }

  const setupRealtimeSubscription = () => {
    if (!user) return

    // Create unique channel name
    const channelName = `notifications:${user.id}:${Date.now()}`

    // Clean up existing subscription if it exists
    if (subscriptionRef.current && channelRef.current) {
      console.log("üîÑ Cleaning up existing subscription:", channelRef.current)
      subscriptionRef.current.unsubscribe()
      subscriptionRef.current = null
    }

    try {
      console.log("üîî Setting up notification subscription:", channelName)

      const channel = supabase
        .channel(channelName)
        .on(
          "postgres_changes",
          {
            event: "INSERT",
            schema: "public",
            table: "notifications",
            filter: `user_id=eq.${user.id}`,
          },
          (payload) => {
            console.log("üÜï New notification received:", payload.new)
            const newNotification = payload.new as Notification
            setNotifications((prev) => [newNotification, ...prev])
            setUnreadCount((prev) => prev + 1)

            // Show a browser notification if permitted
            if ('Notification' in window && Notification.permission === 'granted') {
              new Notification(newNotification.title, {
                body: newNotification.message,
                icon: '/placeholder-logo.png'
              })
            }
          },
        )
        .on(
          "postgres_changes",
          {
            event: "UPDATE",
            schema: "public",
            table: "notifications",
            filter: `user_id=eq.${user.id}`,
          },
          (payload) => {
            console.log("üìù Notification updated:", payload.new)
            const updatedNotification = payload.new as Notification
            setNotifications((prev) => prev.map((n) => (n.id === updatedNotification.id ? updatedNotification : n)))
            // Recalculate unread count
            setNotifications((prev) => {
              const newUnreadCount = prev.filter((n) => !n.is_read).length
              setUnreadCount(newUnreadCount)
              return prev
            })
          },
        )
        .subscribe((status) => {
          console.log("üì° Subscription status:", status)
        })

      subscriptionRef.current = channel
      channelRef.current = channelName
    } catch (error) {
      console.error("‚ùå Error setting up subscription:", error)
    }
  }

  const markAsRead = async (notificationId: string) => {
    if (isTableMissing) return

    try {
      const { error } = await supabase.from("notifications").update({ is_read: true }).eq("id", notificationId)

      if (!error) {
        setNotifications((prev) => prev.map((n) => (n.id === notificationId ? { ...n, is_read: true } : n)))
        setUnreadCount((prev) => Math.max(0, prev - 1))
      }
    } catch (error) {
      console.error("‚ùå Error marking notification as read:", error)
    }
  }

  const markAllAsRead = async () => {
    if (!user || isTableMissing) return

    try {
      const { error } = await supabase
        .from("notifications")
        .update({ is_read: true })
        .eq("user_id", user.id)
        .eq("is_read", false)

      if (!error) {
        setNotifications((prev) => prev.map((n) => ({ ...n, is_read: true })))
        setUnreadCount(0)
      }
    } catch (error) {
      console.error("‚ùå Error marking all notifications as read:", error)
    }
  }

  const clearAllNotifications = async () => {
    if (!user) return
    try {
      await supabase.from("notifications").delete().eq("user_id", user.id)
      setNotifications([])
      setUnreadCount(0)
    } catch (error) {
      console.error("‚ùå Error clearing notifications:", error)
    }
  }

  const handleNotificationClick = async (notification: Notification) => {
    if (!notification.is_read) {
      await markAsRead(notification.id)
    }

    // Open chat for more notification types if related_claim_id exists
    if ((["message", "item_request", "status_update"].includes(notification.type)) && notification.related_claim_id) {
      try {
        // Get the claim details and user names separately
        const { data: claimData } = await supabase
          .from("claims")
          .select("*")
          .eq("id", notification.related_claim_id)
          .single()

        if (claimData) {
          // Get the other user's name
          const otherUserId = claimData.claimer_id === user?.id ? claimData.owner_id : claimData.claimer_id
          const { data: otherUserProfile } = await supabase
            .from("profiles")
            .select("full_name")
            .eq("id", otherUserId)
            .single()

          const otherUserName = otherUserProfile?.full_name || "User"
          onOpenChat(notification.related_claim_id, otherUserName)
          setIsOpen(false)
        }
      } catch (error) {
        console.error("‚ùå Error handling notification click:", error)
      }
    }
  }

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case "message":
        return <MessageCircle className="h-4 w-4" />
      case "item_request":
        return <Package className="h-4 w-4" />
      case "collaboration":
        return <Users className="h-4 w-4" />
      default:
        return <Bell className="h-4 w-4" />
    }
  }

  // Show setup message if table is missing
  if (isTableMissing) {
    return (
      <Dialog open={isOpen} onOpenChange={setIsOpen}>
        <DialogTrigger asChild>
          <Button variant="outline" size="sm" className="relative">
            <AlertCircle className="h-4 w-4 text-orange-500" />
          </Button>
        </DialogTrigger>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <AlertCircle className="h-5 w-5 text-orange-500" />
              Setup Required
            </DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <p className="text-sm text-gray-600">
              The notification system needs to be set up. Please run the complete database setup script to enable
              notifications and collaboration features.
            </p>
            <div className="bg-gray-100 p-3 rounded text-sm">
              <strong>Script to run:</strong>
              <br />
              <code>scripts/setup-complete-database.sql</code>
            </div>
            <Button onClick={() => setIsOpen(false)} className="w-full">
              Got it
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    )
  }

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" size="sm" className="relative">
          <Bell className="h-4 w-4" />
          {unreadCount > 0 && (
            <Badge className="absolute -top-2 -right-2 h-5 w-5 rounded-full p-0 text-xs bg-red-500">
              {unreadCount > 9 ? "9+" : unreadCount}
            </Badge>
          )}
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-md h-[600px] flex flex-col">
        <DialogHeader>
          <div className="flex justify-between items-center">
            <DialogTitle>Notifications</DialogTitle>
            <div className="flex gap-2">
              {unreadCount > 0 && (
                <Button variant="ghost" size="sm" onClick={markAllAsRead}>
                  Mark all read
                </Button>
              )}
              {notifications.length > 0 && (
                <Button variant="ghost" size="sm" onClick={clearAllNotifications} title="Clear all notifications">
                  üóëÔ∏è
                </Button>
              )}
            </div>
          </div>
        </DialogHeader>

        <ScrollArea className="flex-1">
          {loading ? (
            <div className="flex justify-center items-center h-32">
              <div className="text-sm">Loading notifications...</div>
            </div>
          ) : (
            <div className="space-y-2">
              {notifications.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <Bell className="h-8 w-8 mx-auto mb-2 opacity-50" />
                  <p>No notifications yet</p>
                  <p className="text-xs mt-1">You'll get notified when someone requests your items or sends messages</p>
                </div>
              ) : (
                notifications.map((notification) => (
                  <Card
                    key={notification.id}
                    className={`cursor-pointer transition-colors hover:bg-gray-50 ${
                      !notification.is_read ? "border-green-200 bg-green-50" : ""
                    }`}
                    onClick={() => handleNotificationClick(notification)}
                  >
                    <CardContent className="p-4">
                      <div className="flex items-start gap-3">
                        <div className="flex-shrink-0 mt-1">{getNotificationIcon(notification.type)}</div>
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center gap-2 mb-1">
                            <h4 className="font-medium text-sm">{notification.title}</h4>
                            {!notification.is_read && (
                              <div className="w-2 h-2 bg-green-500 rounded-full flex-shrink-0" />
                            )}
                          </div>
                          <p className="text-sm text-gray-600 line-clamp-2">{notification.message}</p>
                          <p className="text-xs text-gray-400 mt-1">
                            {new Date(notification.created_at).toLocaleString()}
                          </p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))
              )}
            </div>
          )}
        </ScrollArea>
      </DialogContent>
    </Dialog>
  )
}

