contexts/AdminAuthContext.tsx.content:
"use client"

import type React from "react"
import { createContext, useContext, useEffect, useState } from "react"
import type { User } from "@supabase/supabase-js"
import { supabase } from "@/lib/supabase"

interface AdminAuthContextType {
  user: User | null
  isAdmin: boolean
  adminRole: string | null
  loading: boolean
  error: string | null
  signIn: (email: string, password: string) => Promise<void>
  signOut: () => Promise<void>
}

const AdminAuthContext = createContext<AdminAuthContextType>({
  user: null,
  isAdmin: false,
  adminRole: null,
  loading: true,
  error: null,
  signIn: async () => {},
  signOut: async () => {},
})

export const useAdminAuth = () => {
  const context = useContext(AdminAuthContext)
  if (!context) {
    throw new Error("useAdminAuth must be used within an AdminAuthProvider")
  }
  return context
}

export function AdminAuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null)
  const [isAdmin, setIsAdmin] = useState(false)
  const [adminRole, setAdminRole] = useState<string | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    // Get initial session
    const getInitialSession = async () => {
      try {
        const {
          data: { session },
          error,
        } = await supabase.auth.getSession()

        if (error) {
          console.error("Error getting session:", error)
          setError(error.message)
        } else if (session?.user) {
          setUser(session.user)
          await checkAdminStatus(session.user.id)
        }
      } catch (err: any) {
        console.error("Unexpected error getting session:", err)
        setError(err.message)
      } finally {
        setLoading(false)
      }
    }

    getInitialSession()

    // Listen for auth changes
    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange(async (event, session) => {
      console.log("Admin auth state changed:", event)
      setUser(session?.user ?? null)
      setError(null)

      if (session?.user) {
        await checkAdminStatus(session.user.id)
      } else {
        setIsAdmin(false)
        setAdminRole(null)
      }

      setLoading(false)
    })

    return () => subscription.unsubscribe()
  }, [])

  const checkAdminStatus = async (userId: string) => {
    try {
      console.log("Checking admin status for user:", userId)

      // Use the safe RPC function that bypasses RLS
      const { data, error } = await supabase.rpc("check_user_admin_status", {
        user_uuid: userId,
      })

      console.log("Admin status check result:", { data, error })

      if (error) {
        console.error("Error with RPC admin check:", error)
        // Fallback: try direct query with service role or simpler approach
        setIsAdmin(false)
        setAdminRole(null)
      } else if (data && data.length > 0) {
        const result = data[0]
        setIsAdmin(result.is_admin)
        setAdminRole(result.is_admin ? result.admin_role : null)
        console.log("Admin status set:", { isAdmin: result.is_admin, role: result.admin_role })
      } else {
        console.log("No admin data returned")
        setIsAdmin(false)
        setAdminRole(null)
      }
    } catch (err) {
      console.error("Unexpected error checking admin status:", err)
      setIsAdmin(false)
      setAdminRole(null)
    }
  }

  const signIn = async (email: string, password: string) => {
    try {
      setLoading(true)
      setError(null)

      console.log("Attempting admin sign in for:", email)

      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      })

      if (error) {
        throw error
      }

      if (data.user) {
        console.log("Authentication successful, checking admin status...")
        setUser(data.user)

        // Use the safe RPC function to check admin status
        const { data: adminCheck, error: adminError } = await supabase.rpc("check_user_admin_status", {
          user_uuid: data.user.id,
        })

        console.log("Admin check during sign in:", { adminCheck, adminError })

        if (adminError || !adminCheck || adminCheck.length === 0 || !adminCheck[0].is_admin) {
          console.log("User is not an admin, signing out...")
          await supabase.auth.signOut()

          const errorMessage = `Access denied. The email "${email}" is not registered as an admin. Please contact your system administrator to get admin access.`
          throw new Error(errorMessage)
        }

        // User is admin, set the status
        const result = adminCheck[0]
        setIsAdmin(true)
        setAdminRole(result.admin_role)
        console.log("Admin sign in successful with role:", result.admin_role)
      }
    } catch (error: any) {
      console.error("Admin sign in error:", error)
      setError(error.message)
      setUser(null)
      setIsAdmin(false)
      setAdminRole(null)
      throw error
    } finally {
      setLoading(false)
    }
  }

  const signOut = async () => {
    try {
      setLoading(true)

      // Check if there's an active session first
      const { data: { session } } = await supabase.auth.getSession()

      if (!session) {
        // No active session, just update local state
        setUser(null)
        setIsAdmin(false)
        setAdminRole(null)
        setError(null)
        console.log("üîí No active admin session, updating local state")
        return
      }

      const { error } = await supabase.auth.signOut()
      if (error) {
        console.error("Error signing out:", error)
        setError(error.message)
      } else {
        setUser(null)
        setIsAdmin(false)
        setAdminRole(null)
        setError(null)
      }
    } catch (err: any) {
      console.error("Unexpected error signing out:", err)
      setError(err.message)
    } finally {
      setLoading(false)
    }
  }

  return (
    <AdminAuthContext.Provider
      value={{
        user,
        isAdmin,
        adminRole,
        loading,
        error,
        signIn,
        signOut,
      }}
    >
      {children}
    </AdminAuthContext.Provider>
  )
}

contexts/AuthContext.tsx.content:
"use client"

import { createContext, useContext, useEffect, useState } from "react"
import type { User } from "@supabase/supabase-js"
import { supabase } from "@/lib/supabase"

interface AuthContextType {
  user: User | null
  loading: boolean
  error: string | null
  signOut: () => Promise<void>
  refreshUser: () => Promise<void>
}

const AuthContext = createContext<AuthContextType>({
  user: null,
  loading: true,
  error: null,
  signOut: async () => {},
  refreshUser: async () => {},
})

export const useAuth = () => {
  const context = useContext(AuthContext)
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider")
  }
  return context
}

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  // üîÅ Get initial session on mount
  useEffect(() => {
    const getInitialSession = async () => {
      try {
        const { data, error } = await supabase.auth.getSession()
        if (error) {
          console.error("Session error:", error)
          setError(error.message)
        }
        setUser(data?.session?.user ?? null)
      } catch (err: any) {
        console.error("Unexpected error:", err)
        setError(err.message)
      } finally {
        setLoading(false)
      }
    }

    getInitialSession()

    // üîÅ Listen to auth changes (login, logout, token refresh)
    const { data: listener } = supabase.auth.onAuthStateChange(async (event, session) => {
      console.log("üîë Auth change:", event)
      setUser(session?.user ?? null)
      setError(null)

      // ‚úÖ Important: stop loading even if session is null
      setLoading(false)

      // üë§ Create profile only on signup
      if (event === "SIGNED_UP" && session?.user) {
        try {
          const { data: existingProfile, error: profileCheckError } = await supabase
            .from("profiles")
            .select("id")
            .eq("id", session.user.id)
            .single()

          if (!existingProfile && !profileCheckError) {
            const { error: insertError } = await supabase.from("profiles").insert({
              id: session.user.id,
              email: session.user.email || "",
              full_name: session.user.user_metadata?.full_name || null,
              phone: session.user.user_metadata?.phone || null,
              location: session.user.user_metadata?.location || null,
            })

            if (insertError) {
              console.error("Error creating profile:", insertError)
            } else {
              console.log("‚úÖ Profile created")
            }
          }
        } catch (err) {
          console.error("Error checking or creating profile:", err)
        }
      }
    })

    return () => {
      listener?.subscription.unsubscribe()
    }
  }, [])

  // üîÅ Manual refresh if needed
  const refreshUser = async () => {
    setLoading(true)
    try {
      const { data, error } = await supabase.auth.getSession()
      if (error) setError(error.message)
      setUser(data?.session?.user ?? null)
    } catch (err: any) {
      setError(err.message)
    } finally {
      setLoading(false)
    }
  }

  const signOut = async () => {
    setLoading(true)
    try {
      // Check if there's an active session first
      const { data: { session } } = await supabase.auth.getSession()

      if (!session) {
        // No active session, just update local state
        setUser(null)
        setError(null)
        console.log("üîí No active session, updating local state")
        return
      }

      const { error } = await supabase.auth.signOut()
      if (error) {
        setError(error.message)
        alert(`Error signing out: ${error.message}`)
      } else {
        setUser(null)
        setError(null)
        console.log("üîí Signed out")
      }
    } catch (err: any) {
      setError(err.message)
      alert(`Unexpected error: ${err.message}`)
    } finally {
      setLoading(false)
    }
  }

  return (
    <AuthContext.Provider value={{ user, loading, error, signOut, refreshUser }}>
      {children}
    </AuthContext.Provider>
  )
}

contexts/LocationContext.tsx.content:
"use client"

import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { getCurrentLocation, Location, getCityCoordinates, MALAWI_CITIES } from '@/lib/locationService';

interface LocationContextType {
  userLocation: Location | null;
  selectedLocation: Location | null;
  locationRadius: number;
  isLoadingLocation: boolean;
  locationError: string | null;
  setUserLocation: (location: Location | null) => void;
  setSelectedLocation: (location: Location | null) => void;
  setLocationRadius: (radius: number) => void;
  getCurrentUserLocation: () => Promise<void>;
  clearLocationError: () => void;
  availableCities: string[];
}

const LocationContext = createContext<LocationContextType | undefined>(undefined);

export const useLocation = () => {
  const context = useContext(LocationContext);
  if (context === undefined) {
    throw new Error('useLocation must be used within a LocationProvider');
  }
  return context;
};

interface LocationProviderProps {
  children: ReactNode;
}

export const LocationProvider: React.FC<LocationProviderProps> = ({ children }) => {
  const [userLocation, setUserLocation] = useState<Location | null>(null);
  const [selectedLocation, setSelectedLocation] = useState<Location | null>(null);
  const [locationRadius, setLocationRadius] = useState(10); // Default 10km radius
  const [isLoadingLocation, setIsLoadingLocation] = useState(false);
  const [locationError, setLocationError] = useState<string | null>(null);

  const availableCities = Object.keys(MALAWI_CITIES);

  const getCurrentUserLocation = async () => {
    setIsLoadingLocation(true);
    setLocationError(null);

    try {
      const location = await getCurrentLocation();
      setUserLocation(location);
      setSelectedLocation(location); // Set as default selected location
      console.log('üìç User location obtained:', location);
    } catch (error) {
      console.error('‚ùå Error getting user location:', error);
      setLocationError(error instanceof Error ? error.message : 'Failed to get location');

      // Fallback to Lilongwe if geolocation fails
      const fallbackLocation = getCityCoordinates('Lilongwe');
      if (fallbackLocation) {
        setSelectedLocation(fallbackLocation);
        console.log('üìç Using fallback location (Lilongwe):', fallbackLocation);
      }
    } finally {
      setIsLoadingLocation(false);
    }
  };

  const clearLocationError = () => {
    setLocationError(null);
  };

  // Try to get user location on mount
  useEffect(() => {
    getCurrentUserLocation();
  }, []);

  const value: LocationContextType = {
    userLocation,
    selectedLocation,
    locationRadius,
    isLoadingLocation,
    locationError,
    setUserLocation,
    setSelectedLocation,
    setLocationRadius,
    getCurrentUserLocation,
    clearLocationError,
    availableCities,
  };

  return (
    <LocationContext.Provider value={value}>
      {children}
    </LocationContext.Provider>
  );
};

lib/locationService.ts.content:
// Location service for handling GPS coordinates and geocoding

export interface Location {
  latitude: number;
  longitude: number;
  address?: string;
  city?: string;
  area?: string;
}

export interface LocationWithDistance extends Location {
  distance: number; // in kilometers
}

// Get user's current location using browser geolocation
export const getCurrentLocation = (): Promise<Location> => {
  return new Promise((resolve, reject) => {
    if (!navigator.geolocation) {
      reject(new Error('Geolocation is not supported by this browser'));
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        resolve({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
        });
      },
      (error) => {
        reject(new Error(`Geolocation error: ${error.message}`));
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 300000, // 5 minutes
      }
    );
  });
};

// Calculate distance between two points using Haversine formula
export const calculateDistance = (
  lat1: number,
  lon1: number,
  lat2: number,
  lon2: number
): number => {
  const R = 6371; // Earth's radius in kilometers
  const dLat = (lat2 - lat1) * (Math.PI / 180);
  const dLon = (lon2 - lon1) * (Math.PI / 180);
  const a =
    Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos(lat1 * (Math.PI / 180)) *
      Math.cos(lat2 * (Math.PI / 180)) *
      Math.sin(dLon / 2) *
      Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

// Format distance for display
export const formatDistance = (distance: number): string => {
  if (distance < 1) {
    return `${Math.round(distance * 1000)}m`;
  }
  return `${distance.toFixed(1)}km`;
};

// Geocode an address to coordinates (using a free geocoding service)
export const geocodeAddress = async (address: string): Promise<Location> => {
  try {
    // Using Nominatim (OpenStreetMap) for free geocoding
    const response = await fetch(
      `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
        address
      )}&limit=1`
    );

    if (!response.ok) {
      throw new Error('Geocoding request failed');
    }

    const data = await response.json();

    if (data.length === 0) {
      throw new Error('Address not found');
    }

    const result = data[0];
    return {
      latitude: parseFloat(result.lat),
      longitude: parseFloat(result.lon),
      address: result.display_name,
    };
  } catch (error) {
    console.error('Geocoding error:', error);
    throw new Error('Failed to geocode address');
  }
};

// Reverse geocode coordinates to address
export const reverseGeocode = async (
  latitude: number,
  longitude: number
): Promise<string> => {
  try {
    const response = await fetch(
      `https://nominatim.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}&zoom=16`
    );

    if (!response.ok) {
      throw new Error('Reverse geocoding request failed');
    }

    const data = await response.json();
    return data.display_name || 'Unknown location';
  } catch (error) {
    console.error('Reverse geocoding error:', error);
    return 'Unknown location';
  }
};

// Common cities in Malawi with their coordinates
export const MALAWI_CITIES = {
  'Lilongwe': { latitude: -13.9626, longitude: 33.7741 },
  'Blantyre': { latitude: -15.7861, longitude: 35.0058 },
  'Mzuzu': { latitude: -11.4587, longitude: 34.0151 },
  'Zomba': { latitude: -15.3833, longitude: 35.3333 },
  'Kasungu': { latitude: -13.0333, longitude: 33.4833 },
  'Mangochi': { latitude: -14.4667, longitude: 35.2667 },
  'Karonga': { latitude: -9.9333, longitude: 33.9333 },
  'Salima': { latitude: -13.7833, longitude: 34.4333 },
  'Nkhotakota': { latitude: -12.9167, longitude: 34.3000 },
  'Dedza': { latitude: -14.3667, longitude: 34.3333 },
};

// Get coordinates for a city
export const getCityCoordinates = (cityName: string): Location | null => {
  const city = MALAWI_CITIES[cityName as keyof typeof MALAWI_CITIES];
  return city ? { ...city, city: cityName } : null;
};

// Validate coordinates
export const isValidCoordinates = (latitude: number, longitude: number): boolean => {
  return (
    latitude >= -90 &&
    latitude <= 90 &&
    longitude >= -180 &&
    longitude <= 180 &&
    !isNaN(latitude) &&
    !isNaN(longitude)
  );
};

// Default radius options for location filtering
export const RADIUS_OPTIONS = [
  { value: 1, label: '1 km' },
  { value: 5, label: '5 km' },
  { value: 10, label: '10 km' },
  { value: 25, label: '25 km' },
  { value: 50, label: '50 km' },
  { value: 100, label: '100 km' },
];

