"use client"

import type React from "react"

import { useState } from "react"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Alert, AlertDescription } from "@/components/ui/alert"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { supabase } from "@/lib/supabase"

export function AdminRegistration() {
  const [email, setEmail] = useState("")
  const [role, setRole] = useState("admin")
  const [loading, setLoading] = useState(false)
  const [message, setMessage] = useState("")
  const [error, setError] = useState("")

  const handlePromoteUser = async (e: React.FormEvent) => {
    e.preventDefault()
    setLoading(true)
    setMessage("")
    setError("")

    try {
      // First, find the user by email
      const { data: userData, error: userError } = await supabase
        .from("profiles")
        .select("id")
        .eq("email", email)
        .single()

      if (userError || !userData) {
        throw new Error("User not found. Make sure they have signed up first.")
      }

      // Check if user is already an admin
      const { data: existingAdmin } = await supabase
        .from("admin_users")
        .select("role")
        .eq("user_id", userData.id)
        .single()

      if (existingAdmin) {
        throw new Error("User is already an admin with role: " + existingAdmin.role)
      }

      // Promote user to admin
      const { error: adminError } = await supabase.from("admin_users").insert({
        user_id: userData.id,
        role: role,
      })

      if (adminError) {
        throw adminError
      }

      setMessage(`Successfully promoted ${email} to ${role}!`)
      setEmail("")
      setRole("admin")
    } catch (err: any) {
      console.error("Error promoting user:", err)
      setError(err.message)
    } finally {
      setLoading(false)
    }
  }

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle>Promote User to Admin</CardTitle>
        <CardDescription>Enter the email of an existing user to promote them to admin</CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={handlePromoteUser} className="space-y-4">
          <div>
            <label htmlFor="email" className="block text-sm font-medium mb-2">
              User Email
            </label>
            <Input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="user@example.com"
              required
            />
          </div>

          <div>
            <label htmlFor="role" className="block text-sm font-medium mb-2">
              Admin Role
            </label>
            <Select value={role} onValueChange={setRole}>
              <SelectTrigger>
                <SelectValue placeholder="Select role" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="admin">Admin</SelectItem>
                <SelectItem value="super_admin">Super Admin</SelectItem>
                <SelectItem value="moderator">Moderator</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <Button type="submit" className="w-full" disabled={loading}>
            {loading ? "Promoting..." : "Promote to Admin"}
          </Button>
        </form>

        {message && (
          <Alert className="mt-4">
            <AlertDescription className="text-green-600">{message}</AlertDescription>
          </Alert>
        )}

        {error && (
          <Alert className="mt-4" variant="destructive">
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}
      </CardContent>
    </Card>
  )
}

components/admin/Analytics.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { supabase } from "@/lib/supabase"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { BarChart3, TrendingUp, Package, Users, Calendar } from "lucide-react"

interface AnalyticsProps {
  stats: {
    totalItems: number
    activeItems: number
    totalUsers: number
    pendingReports: number
    weeklyItems: number
    topCategory: string
  }
}

interface WeeklyData {
  week: string
  items: number
}

interface CategoryData {
  category: string
  count: number
}

interface LocationData {
  location: string
  count: number
}

export default function Analytics({ stats }: AnalyticsProps) {
  const [weeklyData, setWeeklyData] = useState<WeeklyData[]>([])
  const [categoryData, setCategoryData] = useState<CategoryData[]>([])
  const [locationData, setLocationData] = useState<LocationData[]>([])
  const [timeRange, setTimeRange] = useState("30")
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    fetchAnalytics()
  }, [timeRange])

  const fetchAnalytics = async () => {
    try {
      const daysAgo = Number.parseInt(timeRange)
      const startDate = new Date()
      startDate.setDate(startDate.getDate() - daysAgo)

      // Fetch weekly data
      const { data: itemsData } = await supabase
        .from("items")
        .select("created_at")
        .gte("created_at", startDate.toISOString())

      // Group by week
      const weeklyMap = new Map<string, number>()
      itemsData?.forEach((item) => {
        const date = new Date(item.created_at)
        const weekStart = new Date(date.setDate(date.getDate() - date.getDay()))
        const weekKey = weekStart.toISOString().split("T")[0]
        weeklyMap.set(weekKey, (weeklyMap.get(weekKey) || 0) + 1)
      })

      const weekly = Array.from(weeklyMap.entries())
        .map(([week, items]) => ({ week, items }))
        .sort((a, b) => a.week.localeCompare(b.week))

      setWeeklyData(weekly)

      // Fetch category data
      const { data: categoryItems } = await supabase
        .from("items")
        .select("category")
        .gte("created_at", startDate.toISOString())

      const categoryMap = new Map<string, number>()
      categoryItems?.forEach((item) => {
        categoryMap.set(item.category, (categoryMap.get(item.category) || 0) + 1)
      })

      const categories = Array.from(categoryMap.entries())
        .map(([category, count]) => ({ category, count }))
        .sort((a, b) => b.count - a.count)
        .slice(0, 10)

      setCategoryData(categories)

      // Fetch location data
      const { data: locationItems } = await supabase
        .from("items")
        .select("pickup_location")
        .gte("created_at", startDate.toISOString())

      const locationMap = new Map<string, number>()
      locationItems?.forEach((item) => {
        // Extract city/area from pickup location
        const location = item.pickup_location.split(",")[0].trim()
        locationMap.set(location, (locationMap.get(location) || 0) + 1)
      })

      const locations = Array.from(locationMap.entries())
        .map(([location, count]) => ({ location, count }))
        .sort((a, b) => b.count - a.count)
        .slice(0, 10)

      setLocationData(locations)
    } catch (error) {
      console.error("Error fetching analytics:", error)
    } finally {
      setLoading(false)
    }
  }

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg">Loading analytics...</div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <CardTitle className="flex items-center gap-2">
              <BarChart3 className="h-5 w-5" />
              Platform Analytics
            </CardTitle>
            <Select value={timeRange} onValueChange={setTimeRange}>
              <SelectTrigger className="w-40">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="7">Last 7 days</SelectItem>
                <SelectItem value="30">Last 30 days</SelectItem>
                <SelectItem value="90">Last 90 days</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardHeader>
        <CardContent>
          {/* Key Metrics */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
            <div className="text-center p-4 bg-blue-50 rounded-lg">
              <Package className="h-8 w-8 mx-auto mb-2 text-blue-600" />
              <div className="text-2xl font-bold text-blue-600">{stats.totalItems}</div>
              <div className="text-sm text-gray-600">Total Items</div>
            </div>
            <div className="text-center p-4 bg-green-50 rounded-lg">
              <TrendingUp className="h-8 w-8 mx-auto mb-2 text-green-600" />
              <div className="text-2xl font-bold text-green-600">{stats.activeItems}</div>
              <div className="text-sm text-gray-600">Active Items</div>
            </div>
            <div className="text-center p-4 bg-purple-50 rounded-lg">
              <Users className="h-8 w-8 mx-auto mb-2 text-purple-600" />
              <div className="text-2xl font-bold text-purple-600">{stats.totalUsers}</div>
              <div className="text-sm text-gray-600">Total Users</div>
            </div>
            <div className="text-center p-4 bg-orange-50 rounded-lg">
              <Calendar className="h-8 w-8 mx-auto mb-2 text-orange-600" />
              <div className="text-2xl font-bold text-orange-600">{stats.weeklyItems}</div>
              <div className="text-sm text-gray-600">This Week</div>
            </div>
          </div>

          {/* Charts */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Weekly Items Chart */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Items Shared Over Time</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  {weeklyData.map((week, index) => (
                    <div key={week.week} className="flex items-center gap-3">
                      <div className="text-sm text-gray-600 w-20">Week {index + 1}</div>
                      <div className="flex-1 bg-gray-200 rounded-full h-4 relative">
                        <div
                          className="bg-blue-500 h-4 rounded-full"
                          style={{
                            width: `${Math.max(5, (week.items / Math.max(...weeklyData.map((w) => w.items))) * 100)}%`,
                          }}
                        />
                      </div>
                      <div className="text-sm font-medium w-8">{week.items}</div>
                    </div>
                  ))}
                </div>
                {weeklyData.length === 0 && (
                  <div className="text-center py-8 text-gray-500">No data available for the selected period</div>
                )}
              </CardContent>
            </Card>

            {/* Top Categories */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Top Categories</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  {categoryData.map((category, index) => (
                    <div key={category.category} className="flex items-center gap-3">
                      <div className="text-sm text-gray-600 w-4">#{index + 1}</div>
                      <div className="text-sm capitalize flex-1">{category.category.replace("-", " ")}</div>
                      <div className="flex-1 bg-gray-200 rounded-full h-4 relative">
                        <div
                          className="bg-green-500 h-4 rounded-full"
                          style={{
                            width: `${Math.max(5, (category.count / Math.max(...categoryData.map((c) => c.count))) * 100)}%`,
                          }}
                        />
                      </div>
                      <div className="text-sm font-medium w-8">{category.count}</div>
                    </div>
                  ))}
                </div>
                {categoryData.length === 0 && (
                  <div className="text-center py-8 text-gray-500">No category data available</div>
                )}
              </CardContent>
            </Card>

            {/* Top Locations */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Top Locations</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  {locationData.map((location, index) => (
                    <div key={location.location} className="flex items-center gap-3">
                      <div className="text-sm text-gray-600 w-4">#{index + 1}</div>
                      <div className="text-sm flex-1">{location.location}</div>
                      <div className="flex-1 bg-gray-200 rounded-full h-4 relative">
                        <div
                          className="bg-purple-500 h-4 rounded-full"
                          style={{
                            width: `${Math.max(5, (location.count / Math.max(...locationData.map((l) => l.count))) * 100)}%`,
                          }}
                        />
                      </div>
                      <div className="text-sm font-medium w-8">{location.count}</div>
                    </div>
                  ))}
                </div>
                {locationData.length === 0 && (
                  <div className="text-center py-8 text-gray-500">No location data available</div>
                )}
              </CardContent>
            </Card>

            {/* Summary Stats */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Quick Stats</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Most Popular Category</span>
                    <span className="font-medium capitalize">{stats.topCategory.replace("-", " ")}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Completion Rate</span>
                    <span className="font-medium">
                      {stats.totalItems > 0
                        ? Math.round(((stats.totalItems - stats.activeItems) / stats.totalItems) * 100)
                        : 0}
                      %
                    </span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Avg Items per User</span>
                    <span className="font-medium">
                      {stats.totalUsers > 0 ? Math.round((stats.totalItems / stats.totalUsers) * 10) / 10 : 0}
                    </span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Growth This Week</span>
                    <span className="font-medium text-green-600">+{stats.weeklyItems}</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

components/admin/ListingsManager.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { supabase } from "@/lib/supabase"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Badge } from "@/components/ui/badge"
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog"
import { Textarea } from "@/components/ui/textarea"
import { Label } from "@/components/ui/label"
import { Search, Edit, Trash2, MapPin, Calendar, User, Package, AlertCircle } from "lucide-react"
import OptimizedImage from "./OptimizedImage"

interface Item {
  id: string
  title: string
  description: string | null
  category: string
  item_type: "food" | "non-food"
  quantity: string
  condition?: string | null
  expiry_date: string | null
  pickup_location: string
  image_url: string | null
  status: string
  created_at: string
  user_id: string
  owner_name?: string
  owner_email?: string
}

interface ListingsManagerProps {
  onStatsUpdate: () => void
}

export default function ListingsManager({ onStatsUpdate }: ListingsManagerProps) {
  const [items, setItems] = useState<Item[]>([])
  const [filteredItems, setFilteredItems] = useState<Item[]>([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState("")
  const [categoryFilter, setCategoryFilter] = useState("all")
  const [locationFilter, setLocationFilter] = useState("")
  const [statusFilter, setStatusFilter] = useState("all")
  const [selectedItem, setSelectedItem] = useState<Item | null>(null)
  const [editDialogOpen, setEditDialogOpen] = useState(false)
  const [editForm, setEditForm] = useState({
    title: "",
    description: "",
    category: "",
    quantity: "",
    pickup_location: "",
  })

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1)
  const [totalPages, setTotalPages] = useState(1)
  const [totalItems, setTotalItems] = useState(0)
  const itemsPerPage = 20

  useEffect(() => {
    fetchItems()
  }, [currentPage, searchTerm, categoryFilter, locationFilter, statusFilter])

  const fetchItems = async () => {
    try {
      setLoading(true)
      console.log("üîç Starting to fetch items...")

      // Build query with filters - simplified without join
      let query = supabase
        .from("items")
        .select("*", { count: 'exact' })
        .order("created_at", { ascending: false })

      // Apply filters
      if (searchTerm) {
        query = query.or(`title.ilike.%${searchTerm}%,description.ilike.%${searchTerm}%`)
      }

      if (categoryFilter !== "all") {
        query = query.eq("category", categoryFilter)
      }

      if (locationFilter) {
        query = query.ilike("pickup_location", `%${locationFilter}%`)
      }

      if (statusFilter !== "all") {
        query = query.eq("status", statusFilter)
      }

      // Apply pagination
      const from = (currentPage - 1) * itemsPerPage
      const to = from + itemsPerPage - 1
      query = query.range(from, to)

      const { data: itemsData, error, count } = await query

      console.log("üìä Query result:", { itemsData: itemsData?.length, error, count })

      if (error) {
        console.error("Supabase error:", error)
        throw new Error(`Database error: ${error.message || 'Unknown database error'}`)
      }

      // Fetch user profiles separately to avoid join issues
      const userIds = [...new Set(itemsData?.map(item => item.user_id) || [])]
      let profilesMap: Record<string, any> = {}

      console.log("üë• User IDs to fetch:", userIds)

      if (userIds.length > 0) {
        const { data: profilesData, error: profilesError } = await supabase
          .from("profiles")
          .select("id, full_name, email")
          .in("id", userIds)

        console.log("üë• Profiles result:", { profilesData: profilesData?.length, profilesError })

        if (profilesError) {
          console.warn("Error fetching profiles:", profilesError)
        } else {
          profilesMap = profilesData?.reduce((acc, profile) => {
            acc[profile.id] = profile
            return acc
          }, {} as Record<string, any>) || {}
        }
      }

      // Transform data to include owner info
      const itemsWithOwners = itemsData?.map((item: any) => ({
          ...item,
        owner_name: profilesMap[item.user_id]?.full_name || "Unknown",
        owner_email: profilesMap[item.user_id]?.email || "Unknown",
        })) || []

      // Debug image URLs
      console.log("üñºÔ∏è Items with image URLs:", itemsWithOwners.map(item => ({
        id: item.id,
        title: item.title,
        image_url: item.image_url,
        hasImage: !!item.image_url
      })))

      setItems(itemsWithOwners)
      setTotalItems(count || 0)
      setTotalPages(Math.ceil((count || 0) / itemsPerPage))

      console.log("‚úÖ Successfully fetched items:", {
        itemsCount: itemsWithOwners.length,
        totalItems: count,
        totalPages: Math.ceil((count || 0) / itemsPerPage)
      })
    } catch (error) {
      console.error("Error fetching items:", error)
      console.error("Error type:", typeof error)
      console.error("Error message:", error instanceof Error ? error.message : String(error))
      console.error("Full error object:", JSON.stringify(error, null, 2))

      setItems([])
      setTotalItems(0)
      setTotalPages(0)
    } finally {
      setLoading(false)
    }
  }

  // Reset to first page when filters change
  useEffect(() => {
    setCurrentPage(1)
  }, [searchTerm, categoryFilter, locationFilter, statusFilter])

  const handleEdit = (item: Item) => {
    setSelectedItem(item)
    setEditForm({
      title: item.title,
      description: item.description || "",
      category: item.category,
      quantity: item.quantity,
      pickup_location: item.pickup_location,
    })
    setEditDialogOpen(true)
  }

  const handleSaveEdit = async () => {
    if (!selectedItem) return

    try {
      const { error } = await supabase
        .from("items")
        .update({
          title: editForm.title,
          description: editForm.description || null,
          category: editForm.category,
          quantity: editForm.quantity,
          pickup_location: editForm.pickup_location,
        })
        .eq("id", selectedItem.id)

      if (error) throw error

      await fetchItems()
      onStatsUpdate()
      setEditDialogOpen(false)
      alert("Item updated successfully!")
    } catch (error: any) {
      console.error("Error updating item:", error)
      alert(`Error updating item: ${error.message}`)
    }
  }

  const handleDelete = async (itemId: string) => {
    if (!confirm("Are you sure you want to delete this item? This action cannot be undone.")) {
      return
    }

    try {
      const { error } = await supabase.from("items").delete().eq("id", itemId)

      if (error) throw error

      await fetchItems()
      onStatsUpdate()
      alert("Item deleted successfully!")
    } catch (error: any) {
      console.error("Error deleting item:", error)
      alert(`Error deleting item: ${error.message}`)
    }
  }

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      available: { color: "bg-green-100 text-green-800", label: "Available" },
      requested: { color: "bg-yellow-100 text-yellow-800", label: "Requested" },
      reserved: { color: "bg-blue-100 text-blue-800", label: "Reserved" },
      completed: { color: "bg-gray-100 text-gray-800", label: "Completed" },
      cancelled: { color: "bg-red-100 text-red-800", label: "Cancelled" },
    }
    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.available
    return <Badge className={config.color}>{config.label}</Badge>
  }

  const categories = [
    "all",
    "fruits",
    "vegetables",
    "grains",
    "dairy",
    "meat",
    "prepared",
    "clothing",
    "shoes",
    "household",
    "electronics",
    "books",
    "toys",
    "baby-items",
    "other",
  ]

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-base sm:text-lg lg:text-xl">Loading listings...</div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="text-xl sm:text-2xl lg:text-3xl">Manage Listings</CardTitle>
        </CardHeader>
        <CardContent>
          {/* Filters */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <Input
                placeholder="Search items..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 text-sm sm:text-base"
              />
            </div>

            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger className="text-sm sm:text-base">
                <SelectValue placeholder="Category" />
              </SelectTrigger>
              <SelectContent>
                {categories.map((category) => (
                  <SelectItem key={category} value={category}>
                    {category.charAt(0).toUpperCase() + category.slice(1).replace("-", " ")}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Input
              placeholder="Filter by location..."
              value={locationFilter}
              onChange={(e) => setLocationFilter(e.target.value)}
              className="text-sm sm:text-base"
            />

            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="text-sm sm:text-base">
                <SelectValue placeholder="Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="available">Available</SelectItem>
                <SelectItem value="requested">Requested</SelectItem>
                <SelectItem value="reserved">Reserved</SelectItem>
                <SelectItem value="completed">Completed</SelectItem>
                <SelectItem value="cancelled">Cancelled</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Results Summary */}
          <div className="mb-4 flex justify-between items-center">
            <p className="text-sm sm:text-base text-gray-600">
              Showing {items.length} of {totalItems} items
            </p>
            {totalPages > 1 && (
              <div className="flex items-center gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                  disabled={currentPage === 1}
                >
                  Previous
                </Button>
                <span className="text-sm text-gray-600">
                  Page {currentPage} of {totalPages}
                </span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                  disabled={currentPage === totalPages}
                >
                  Next
                </Button>
              </div>
            )}
          </div>

          {/* Items Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {items.map((item) => (
              <Card 
                key={item.id} 
                clickable={true}
                onClick={() => handleEdit(item)}
              >
                  <div className="relative h-32 w-full bg-gray-100">
                    {item.image_url && item.image_url.trim() !== '' ? (
                      <OptimizedImage
                        src={item.image_url}
                      alt={item.title}
                      fill
                      className="object-cover rounded-t-lg"
                    />
                    ) : (
                      <div className="flex items-center justify-center h-full bg-gray-100 rounded-t-lg">
                        <div className="text-center">
                          <Package className="h-8 w-8 text-gray-400 mx-auto mb-2" />
                          <span className="text-xs text-gray-500">No image</span>
                        </div>
                      </div>
                    )}
                  </div>
                <CardContent className="p-4">
                  <div className="flex justify-between items-start mb-2">
                    <div className="relative group flex-1 min-w-0">
                      <h3 className="font-medium text-sm sm:text-base lg:text-lg line-clamp-1 group-hover:line-clamp-none group-hover:bg-gray-50 group-hover:p-2 group-hover:rounded group-hover:absolute group-hover:z-10 group-hover:shadow-lg group-hover:border group-hover:min-w-full group-hover:max-w-xs group-hover:transition-all group-hover:duration-200">
                        {item.title}
                      </h3>
                    </div>
                    {getStatusBadge(item.status)}
                  </div>

                  <div className="relative group mb-2">
                    <p className="text-xs sm:text-sm lg:text-base text-gray-600 line-clamp-2 group-hover:line-clamp-none group-hover:bg-gray-50 group-hover:p-2 group-hover:rounded group-hover:absolute group-hover:z-10 group-hover:shadow-lg group-hover:border group-hover:min-w-full group-hover:max-w-xs group-hover:transition-all group-hover:duration-200">
                      {item.description}
                    </p>
                  </div>

                  <div className="space-y-1 text-xs sm:text-sm text-gray-500 mb-3">
                    <div className="flex items-center gap-1">
                      <Package className="h-3 w-3" />
                      <span>
                        {item.category} ‚Ä¢ {item.quantity}
                      </span>
                    </div>
                    <div className="flex items-center gap-1">
                      <MapPin className="h-3 w-3" />
                      <span>{item.pickup_location}</span>
                    </div>
                    <div className="flex items-center gap-1">
                      <User className="h-3 w-3" />
                      <span>{item.owner_name}</span>
                    </div>
                    <div className="flex items-center gap-1">
                      <Calendar className="h-3 w-3" />
                      <span>{new Date(item.created_at).toLocaleDateString()}</span>
                    </div>
                  </div>

                  <div className="flex gap-2">
                    <Button 
                      size="sm" 
                      variant="outline" 
                      onClick={(e) => {
                        e.stopPropagation();
                        handleEdit(item);
                      }} 
                      className="flex-1 text-xs sm:text-sm"
                    >
                      <Edit className="h-3 w-3 mr-1" />
                      Edit
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDelete(item.id);
                      }}
                      className="text-red-600 hover:text-red-700 text-xs sm:text-sm"
                    >
                      <Trash2 className="h-3 w-3" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {items.length === 0 && !loading && (
            <div className="text-center py-12">
              <AlertCircle className="h-12 w-12 mx-auto mb-4 text-gray-400" />
              <h3 className="text-lg sm:text-xl lg:text-2xl font-medium text-gray-900 mb-2">No items found</h3>
              <p className="text-sm sm:text-base lg:text-lg text-gray-600">Try adjusting your filters to see more results.</p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Edit Dialog */}
      <Dialog open={editDialogOpen} onOpenChange={setEditDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle className="text-lg sm:text-xl lg:text-2xl">Edit Item</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="title" className="text-sm sm:text-base">Title</Label>
              <Input
                id="title"
                value={editForm.title}
                onChange={(e) => setEditForm({ ...editForm, title: e.target.value })}
                className="text-sm sm:text-base"
              />
            </div>
            <div>
              <Label htmlFor="description" className="text-sm sm:text-base">Description</Label>
              <Textarea
                id="description"
                value={editForm.description}
                onChange={(e) => setEditForm({ ...editForm, description: e.target.value })}
                rows={3}
                className="text-sm sm:text-base"
              />
            </div>
            <div>
              <Label htmlFor="category" className="text-sm sm:text-base">Category</Label>
              <Select
                value={editForm.category}
                onValueChange={(value) => setEditForm({ ...editForm, category: value })}
              >
                <SelectTrigger className="text-sm sm:text-base">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {categories.slice(1).map((category) => (
                    <SelectItem key={category} value={category}>
                      {category.charAt(0).toUpperCase() + category.slice(1).replace("-", " ")}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="quantity" className="text-sm sm:text-base">Quantity</Label>
              <Input
                id="quantity"
                value={editForm.quantity}
                onChange={(e) => setEditForm({ ...editForm, quantity: e.target.value })}
                className="text-sm sm:text-base"
              />
            </div>
            <div>
              <Label htmlFor="pickup_location" className="text-sm sm:text-base">Pickup Location</Label>
              <Input
                id="pickup_location"
                value={editForm.pickup_location}
                onChange={(e) => setEditForm({ ...editForm, pickup_location: e.target.value })}
                className="text-sm sm:text-base"
              />
            </div>
            <div className="flex gap-2">
              <Button variant="outline" onClick={() => setEditDialogOpen(false)} className="flex-1 text-sm sm:text-base">
                Cancel
              </Button>
              <Button onClick={handleSaveEdit} className="flex-1 text-sm sm:text-base">
                Save Changes
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  )
}

components/admin/OptimizedImage.tsx.content:
"use client"

import { useState } from "react"
import Image from "next/image"
import { Skeleton } from "@/components/ui/skeleton"
import { Package } from "lucide-react"

interface OptimizedImageProps {
  src: string | null
  alt: string
  className?: string
  fill?: boolean
  width?: number
  height?: number
}

export default function OptimizedImage({ 
  src, 
  alt, 
  className = "", 
  fill = false, 
  width, 
  height 
}: OptimizedImageProps) {
  const [isLoading, setIsLoading] = useState(true)
  const [hasError, setHasError] = useState(false)

  // Debug logging
  console.log("üñºÔ∏è OptimizedImage render:", { src, alt, hasError, isLoading })

  if (!src || src.trim() === '') {
    return (
      <div className={`bg-gray-200 flex items-center justify-center ${className}`}>
        <Package className="h-8 w-8 text-gray-400" />
      </div>
    )
  }

  if (hasError) {
    return (
      <div className={`bg-gray-200 flex items-center justify-center ${className}`}>
        <span className="text-gray-400 text-sm">Image failed to load</span>
      </div>
    )
  }

  return (
    <div className="relative">
      {isLoading && (
        <Skeleton className={`absolute inset-0 ${className}`} />
      )}
      <Image
        src={src}
        alt={alt}
        fill={fill}
        width={width}
        height={height}
        className={`${className} ${isLoading ? 'opacity-0' : 'opacity-100'} transition-opacity duration-300`}
        onLoad={() => {
          console.log("‚úÖ Image loaded successfully:", src)
          setIsLoading(false)
        }}
        onError={(e) => {
          console.error("‚ùå Image failed to load:", src, e)
          setIsLoading(false)
          setHasError(true)
        }}
        sizes="(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw"
        priority={false}
      />
    </div>
  )
}

components/admin/ReportsManager.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { supabase } from "@/lib/supabase"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog"
import { Flag, CheckCircle, Trash2, Eye, Calendar, User, MessageSquare } from "lucide-react"
import Image from "next/image"

interface Report {
  id: string
  item_id: string
  reporter_id: string
  reason: string
  description: string | null
  status: string
  created_at: string
  item?: {
    id: string
    title: string
    description: string | null
    image_url: string | null
    category: string
    pickup_location: string
    owner_name?: string
  }
  reporter_name?: string
}

interface ReportsManagerProps {
  onStatsUpdate: () => void
}

export default function ReportsManager({ onStatsUpdate }: ReportsManagerProps) {
  const [reports, setReports] = useState<Report[]>([])
  const [loading, setLoading] = useState(true)
  const [selectedReport, setSelectedReport] = useState<Report | null>(null)
  const [detailsOpen, setDetailsOpen] = useState(false)
  const [statusFilter, setStatusFilter] = useState("pending")

  useEffect(() => {
    fetchReports()
  }, [statusFilter])

  const fetchReports = async () => {
    try {
      // Fetch reports
      const { data: reportsData, error } = await supabase
        .from("reports")
        .select("*")
        .eq("status", statusFilter)
        .order("created_at", { ascending: false })

      if (error) throw error

      // Get related items and user information
      const reportsWithDetails = await Promise.all(
        (reportsData || []).map(async (report) => {
          // Get item details
          const { data: itemData } = await supabase.from("items").select("*").eq("id", report.item_id).single()

          // Get item owner name
          let ownerName = "Unknown"
          if (itemData) {
            const { data: ownerProfile } = await supabase
              .from("profiles")
              .select("full_name")
              .eq("id", itemData.user_id)
              .single()
            ownerName = ownerProfile?.full_name || "Unknown"
          }

          // Get reporter name
          const { data: reporterProfile } = await supabase
            .from("profiles")
            .select("full_name")
            .eq("id", report.reporter_id)
            .single()

          return {
            ...report,
            item: itemData ? { ...itemData, owner_name: ownerName } : null,
            reporter_name: reporterProfile?.full_name || "Anonymous",
          }
        }),
      )

      setReports(reportsWithDetails)
    } catch (error) {
      console.error("Error fetching reports:", error)
    } finally {
      setLoading(false)
    }
  }

  const handleMarkAsReviewed = async (reportId: string) => {
    try {
      const { error } = await supabase.from("reports").update({ status: "reviewed" }).eq("id", reportId)

      if (error) throw error

      await fetchReports()
      onStatsUpdate()
      alert("Report marked as reviewed!")
    } catch (error: any) {
      console.error("Error updating report:", error)
      alert(`Error updating report: ${error.message}`)
    }
  }

  const handleDeleteItem = async (itemId: string, reportId: string) => {
    if (!confirm("Are you sure you want to delete this reported item? This action cannot be undone.")) {
      return
    }

    try {
      // Delete the item
      const { error: deleteError } = await supabase.from("items").delete().eq("id", itemId)

      if (deleteError) throw deleteError

      // Mark report as resolved
      const { error: updateError } = await supabase.from("reports").update({ status: "resolved" }).eq("id", reportId)

      if (updateError) throw updateError

      await fetchReports()
      onStatsUpdate()
      alert("Item deleted and report resolved!")
    } catch (error: any) {
      console.error("Error deleting item:", error)
      alert(`Error deleting item: ${error.message}`)
    }
  }

  const getReasonBadge = (reason: string) => {
    const reasonConfig = {
      inappropriate: { color: "bg-red-100 text-red-800", label: "Inappropriate" },
      spam: { color: "bg-orange-100 text-orange-800", label: "Spam" },
      fake: { color: "bg-yellow-100 text-yellow-800", label: "Fake" },
      offensive: { color: "bg-purple-100 text-purple-800", label: "Offensive" },
      other: { color: "bg-gray-100 text-gray-800", label: "Other" },
    }
    const config = reasonConfig[reason as keyof typeof reasonConfig] || reasonConfig.other
    return <Badge className={config.color}>{config.label}</Badge>
  }

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      pending: { color: "bg-yellow-100 text-yellow-800", label: "Pending" },
      reviewed: { color: "bg-blue-100 text-blue-800", label: "Reviewed" },
      resolved: { color: "bg-green-100 text-green-800", label: "Resolved" },
    }
    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.pending
    return <Badge className={config.color}>{config.label}</Badge>
  }

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg">Loading reports...</div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Flag className="h-5 w-5" />
            Reported Items
          </CardTitle>
        </CardHeader>
        <CardContent>
          {/* Status Filter */}
          <div className="flex gap-2 mb-6">
            {["pending", "reviewed", "resolved"].map((status) => (
              <Button
                key={status}
                variant={statusFilter === status ? "default" : "outline"}
                size="sm"
                onClick={() => setStatusFilter(status)}
              >
                {status.charAt(0).toUpperCase() + status.slice(1)}
              </Button>
            ))}
          </div>

          {/* Reports List */}
          <div className="space-y-4">
            {reports.map((report) => (
              <Card key={report.id} className="border-l-4 border-l-red-500">
                <CardContent className="p-4">
                  <div className="flex justify-between items-start mb-3">
                    <div className="flex items-center gap-2">
                      {getReasonBadge(report.reason)}
                      {getStatusBadge(report.status)}
                    </div>
                    <div className="flex gap-2">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => {
                          setSelectedReport(report)
                          setDetailsOpen(true)
                        }}
                      >
                        <Eye className="h-3 w-3 mr-1" />
                        Details
                      </Button>
                      {report.status === "pending" && (
                        <>
                          <Button size="sm" variant="outline" onClick={() => handleMarkAsReviewed(report.id)}>
                            <CheckCircle className="h-3 w-3 mr-1" />
                            Mark Reviewed
                          </Button>
                          {report.item && (
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => handleDeleteItem(report.item!.id, report.id)}
                              className="text-red-600 hover:text-red-700"
                            >
                              <Trash2 className="h-3 w-3 mr-1" />
                              Delete Item
                            </Button>
                          )}
                        </>
                      )}
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {/* Report Info */}
                    <div>
                      <h4 className="font-medium text-sm mb-2">Report Details</h4>
                      <div className="space-y-1 text-xs text-gray-600">
                        <div className="flex items-center gap-1">
                          <User className="h-3 w-3" />
                          <span>Reported by: {report.reporter_name}</span>
                        </div>
                        <div className="flex items-center gap-1">
                          <Calendar className="h-3 w-3" />
                          <span>{new Date(report.created_at).toLocaleString()}</span>
                        </div>
                        {report.description && (
                          <div className="flex items-start gap-1">
                            <MessageSquare className="h-3 w-3 mt-0.5" />
                            <span className="line-clamp-2">{report.description}</span>
                          </div>
                        )}
                      </div>
                    </div>

                    {/* Item Info */}
                    {report.item ? (
                      <div>
                        <h4 className="font-medium text-sm mb-2">Reported Item</h4>
                        <div className="flex gap-3">
                          {report.item.image_url && (
                            <div className="relative w-16 h-16 flex-shrink-0">
                              <Image
                                src={report.item.image_url || "/placeholder.svg"}
                                alt={report.item.title}
                                fill
                                className="object-cover rounded"
                              />
                            </div>
                          )}
                          <div className="flex-1 min-w-0">
                            <h5 className="font-medium text-sm line-clamp-1">{report.item.title}</h5>
                            <p className="text-xs text-gray-600 line-clamp-2">{report.item.description}</p>
                            <div className="text-xs text-gray-500 mt-1">
                              <span>
                                {report.item.category} ‚Ä¢ {report.item.pickup_location}
                              </span>
                              <br />
                              <span>Owner: {report.item.owner_name}</span>
                            </div>
                          </div>
                        </div>
                      </div>
                    ) : (
                      <div>
                        <h4 className="font-medium text-sm mb-2">Reported Item</h4>
                        <p className="text-xs text-red-600">Item has been deleted</p>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {reports.length === 0 && (
            <div className="text-center py-12">
              <Flag className="h-12 w-12 mx-auto mb-4 text-gray-400" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No {statusFilter} reports</h3>
              <p className="text-gray-600">
                {statusFilter === "pending"
                  ? "Great! No reports need your attention right now."
                  : `No ${statusFilter} reports found.`}
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Report Details Dialog */}
      <Dialog open={detailsOpen} onOpenChange={setDetailsOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Report Details</DialogTitle>
          </DialogHeader>
          {selectedReport && (
            <div className="space-y-6">
              {/* Report Information */}
              <div>
                <h3 className="font-medium mb-3">Report Information</h3>
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="text-gray-600">Reason:</span>
                    <div className="mt-1">{getReasonBadge(selectedReport.reason)}</div>
                  </div>
                  <div>
                    <span className="text-gray-600">Status:</span>
                    <div className="mt-1">{getStatusBadge(selectedReport.status)}</div>
                  </div>
                  <div>
                    <span className="text-gray-600">Reporter:</span>
                    <div className="mt-1">{selectedReport.reporter_name}</div>
                  </div>
                  <div>
                    <span className="text-gray-600">Date:</span>
                    <div className="mt-1">{new Date(selectedReport.created_at).toLocaleString()}</div>
                  </div>
                </div>
                {selectedReport.description && (
                  <div className="mt-4">
                    <span className="text-gray-600">Description:</span>
                    <p className="mt-1 text-sm bg-gray-50 p-3 rounded">{selectedReport.description}</p>
                  </div>
                )}
              </div>

              {/* Item Information */}
              {selectedReport.item && (
                <div>
                  <h3 className="font-medium mb-3">Reported Item</h3>
                  <div className="border rounded-lg p-4">
                    <div className="flex gap-4">
                      {selectedReport.item.image_url && (
                        <div className="relative w-24 h-24 flex-shrink-0">
                          <Image
                            src={selectedReport.item.image_url || "/placeholder.svg"}
                            alt={selectedReport.item.title}
                            fill
                            className="object-cover rounded"
                          />
                        </div>
                      )}
                      <div className="flex-1">
                        <h4 className="font-medium">{selectedReport.item.title}</h4>
                        <p className="text-sm text-gray-600 mt-1">{selectedReport.item.description}</p>
                        <div className="text-sm text-gray-500 mt-2">
                          <p>Category: {selectedReport.item.category}</p>
                          <p>Location: {selectedReport.item.pickup_location}</p>
                          <p>Owner: {selectedReport.item.owner_name}</p>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* Actions */}
              {selectedReport.status === "pending" && (
                <div className="flex gap-2">
                  <Button
                    onClick={() => {
                      handleMarkAsReviewed(selectedReport.id)
                      setDetailsOpen(false)
                    }}
                    className="flex-1"
                  >
                    <CheckCircle className="h-4 w-4 mr-2" />
                    Mark as Reviewed
                  </Button>
                  {selectedReport.item && (
                    <Button
                      variant="outline"
                      onClick={() => {
                        handleDeleteItem(selectedReport.item!.id, selectedReport.id)
                        setDetailsOpen(false)
                      }}
                      className="flex-1 text-red-600 hover:text-red-700"
                    >
                      <Trash2 className="h-4 w-4 mr-2" />
                      Delete Item
                    </Button>
                  )}
                </div>
              )}
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  )
}

components/admin/StoriesManager.tsx.content:
"use client"

import { useEffect, useState } from "react"
import { supabase } from "@/lib/supabase"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Dialog, DialogTrigger, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog"
import { Input } from "@/components/ui/input"
import { Textarea } from "@/components/ui/textarea"
import { Edit, Trash2, Loader2, CheckCircle, XCircle } from "lucide-react"

export default function StoriesManager() {
  const [stories, setStories] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [actionLoading, setActionLoading] = useState<string | null>(null)
  const [editDialogOpen, setEditDialogOpen] = useState(false)
  const [editStory, setEditStory] = useState<any | null>(null)
  const [editForm, setEditForm] = useState({
    name: "",
    location: "",
    story: "",
  })
  const [editLoading, setEditLoading] = useState(false)
  const [deleteLoading, setDeleteLoading] = useState<string | null>(null)

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1)
  const [totalPages, setTotalPages] = useState(1)
  const [totalStories, setTotalStories] = useState(0)
  const storiesPerPage = 10

  useEffect(() => {
    fetchStories()
  }, [currentPage])

  const fetchStories = async () => {
    try {
      setLoading(true)
      const from = (currentPage - 1) * storiesPerPage
      const to = from + storiesPerPage - 1

      const { data, error, count } = await supabase
        .from("stories")
        .select("*", { count: 'exact' })
        .order("created_at", { ascending: false })
        .range(from, to)

      if (error) throw error

      setStories(data || [])
      setTotalStories(count || 0)
      setTotalPages(Math.ceil((count || 0) / storiesPerPage))
    } catch (error) {
      console.error("Error fetching stories:", error)
      console.error("Error details:", {
        message: error instanceof Error ? error.message : 'Unknown error',
        stack: error instanceof Error ? error.stack : undefined,
        error
      })
      setStories([])
      setTotalStories(0)
      setTotalPages(0)
    } finally {
      setLoading(false)
    }
  }

  const handleApprove = async (id: string) => {
    setActionLoading(id)
    await supabase.from("stories").update({ status: "approved" }).eq("id", id)
    await fetchStories()
    setActionLoading(null)
  }

  const handleReject = async (id: string) => {
    setActionLoading(id)
    await supabase.from("stories").update({ status: "rejected" }).eq("id", id)
    await fetchStories()
    setActionLoading(null)
  }

  const handleEdit = (story: any) => {
    setEditStory(story)
    setEditForm({
      name: story.name || "",
      location: story.location || "",
      story: story.story || "",
    })
    setEditDialogOpen(true)
  }

  const handleSaveEdit = async () => {
    if (!editStory) return
    setEditLoading(true)
    await supabase.from("stories").update({
      name: editForm.name,
      location: editForm.location,
      story: editForm.story,
    }).eq("id", editStory.id)
    setEditLoading(false)
    setEditDialogOpen(false)
    setEditStory(null)
    await fetchStories()
  }

  const handleDelete = async (id: string) => {
    if (!confirm("Are you sure you want to delete this story? This action cannot be undone.")) return
    setDeleteLoading(id)
    await supabase.from("stories").delete().eq("id", id)
    setDeleteLoading(null)
    await fetchStories()
  }

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="flex justify-center items-center h-32">
          <div className="flex items-center gap-2">
            <Loader2 className="h-6 w-6 animate-spin text-green-600" />
            <span className="text-gray-600">Loading stories...</span>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {/* Results Summary */}
      <div className="flex justify-between items-center">
        <p className="text-sm text-gray-600">
          Showing {stories.length} of {totalStories} stories
        </p>
        {totalPages > 1 && (
          <div className="flex items-center gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
              disabled={currentPage === 1}
            >
              Previous
            </Button>
            <span className="text-sm text-gray-600">
              Page {currentPage} of {totalPages}
            </span>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
              disabled={currentPage === totalPages}
            >
              Next
            </Button>
          </div>
        )}
      </div>

      {stories.length === 0 && !loading && (
        <div className="text-center py-12">
          <div className="text-gray-400 text-lg">No stories submitted yet.</div>
        </div>
      )}
      {stories.map(story => (
        <Card key={story.id} className="border-l-4 mb-4 border-l-green-500">
          <CardHeader>
            <CardTitle>
              {story.name} <span className="text-gray-400 text-sm">({story.location})</span>
              <Badge className="ml-2" variant={
                story.status === "approved"
                  ? "default"
                  : story.status === "pending"
                  ? "secondary"
                  : "destructive"
              }>
                {story.status}
              </Badge>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <blockquote className="italic mb-2">"{story.story}"</blockquote>
            <div className="flex flex-wrap gap-2 mt-2">
              {story.status === "pending" && (
                <>
                  <Button size="sm" onClick={() => handleApprove(story.id)} disabled={actionLoading === story.id} className="flex items-center gap-1 min-w-[90px]">
                    {actionLoading === story.id ? <Loader2 className="animate-spin h-4 w-4" /> : <CheckCircle className="h-3 w-3" />}
                    {actionLoading === story.id ? "Approving..." : "Approve"}
                  </Button>
                  <Button size="sm" variant="destructive" onClick={() => handleReject(story.id)} disabled={actionLoading === story.id} className="flex items-center gap-1 min-w-[90px]">
                    {actionLoading === story.id ? <Loader2 className="animate-spin h-4 w-4" /> : <XCircle className="h-3 w-3" />}
                    {actionLoading === story.id ? "Rejecting..." : "Reject"}
                  </Button>
                </>
              )}
              {story.status === "approved" && (
                <span className="text-green-600 font-semibold">Approved</span>
              )}
              {story.status === "rejected" && (
                <span className="text-red-600 font-semibold">Rejected</span>
              )}
              <Button size="sm" variant="outline" onClick={() => handleEdit(story)} className="flex items-center gap-1 min-w-[90px]">
                <Edit className="h-3 w-3" /> Edit
              </Button>
              <Button size="sm" variant="outline" onClick={() => handleDelete(story.id)} className="text-red-600 hover:text-red-700 flex items-center gap-1 min-w-[90px]" disabled={deleteLoading === story.id}>
                {deleteLoading === story.id ? <Loader2 className="animate-spin h-4 w-4" /> : <Trash2 className="h-3 w-3" />}
                {deleteLoading === story.id ? "Deleting..." : "Delete"}
              </Button>
            </div>
          </CardContent>
        </Card>
      ))}
      <Dialog open={editDialogOpen} onOpenChange={setEditDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit Story</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-1">Name</label>
              <Input
                value={editForm.name}
                onChange={e => setEditForm({ ...editForm, name: e.target.value })}
                placeholder="Name"
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">Location</label>
              <Input
                value={editForm.location}
                onChange={e => setEditForm({ ...editForm, location: e.target.value })}
                placeholder="Location"
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">Story</label>
              <Textarea
                value={editForm.story}
                onChange={e => setEditForm({ ...editForm, story: e.target.value })}
                placeholder="Story"
                rows={5}
              />
            </div>
          </div>
          <DialogFooter className="mt-4">
            <Button variant="outline" onClick={() => setEditDialogOpen(false)}>
              Cancel
            </Button>
            <Button onClick={handleSaveEdit} disabled={editLoading}>
              {editLoading ? "Saving..." : "Save Changes"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

components/admin/UsersManager.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { supabase } from "@/lib/supabase"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Badge } from "@/components/ui/badge"
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog"
import { Textarea } from "@/components/ui/textarea"
import { Label } from "@/components/ui/label"
import { Users, Search, UserX, UserCheck, Mail, Calendar, Package, AlertTriangle } from "lucide-react"

interface UserStats {
  user_id: string
  full_name: string | null
  email: string
  total_items: number
  active_items: number
  completed_items: number
  reports_count: number
  last_activity: string | null
  is_suspended?: boolean
  suspension_reason?: string
}

export default function UsersManager() {
  const [users, setUsers] = useState<UserStats[]>([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState("")
  const [suspendDialogOpen, setSuspendDialogOpen] = useState(false)
  const [selectedUser, setSelectedUser] = useState<UserStats | null>(null)
  const [suspensionForm, setSuspensionForm] = useState({
    reason: "",
    isPermanent: false,
    suspendedUntil: "",
  })

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1)
  const [totalPages, setTotalPages] = useState(1)
  const [totalUsers, setTotalUsers] = useState(0)
  const usersPerPage = 15

  useEffect(() => {
    fetchUsers()
  }, [currentPage, searchTerm])

  const fetchUsers = async () => {
    try {
      setLoading(true)

      // Get user stats with pagination
      const { data: userStats, error, count } = await supabase
        .rpc("get_user_stats", {}, { count: 'exact' })
        .range((currentPage - 1) * usersPerPage, currentPage * usersPerPage - 1)

      if (error) throw error

      // Get all user IDs for suspension check
      const userIds = userStats?.map(user => user.user_id) || []

      // Get suspensions in one query
      const { data: suspensions } = await supabase
        .from("user_suspensions")
        .select("user_id, reason, suspended_until, is_permanent")
        .in("user_id", userIds)
        .or(`is_permanent.eq.true,suspended_until.gte.${new Date().toISOString()}`)
        .order("created_at", { ascending: false })

      // Create suspension map
      const suspensionMap = new Map()
      suspensions?.forEach(suspension => {
        if (!suspensionMap.has(suspension.user_id)) {
          suspensionMap.set(suspension.user_id, suspension)
        }
      })

      // Combine user stats with suspension info
      const usersWithSuspensions = userStats?.map(user => ({
        ...user,
        is_suspended: suspensionMap.has(user.user_id),
        suspension_reason: suspensionMap.get(user.user_id)?.reason || null,
      })) || []

      setUsers(usersWithSuspensions)
      setTotalUsers(count || 0)
      setTotalPages(Math.ceil((count || 0) / usersPerPage))
    } catch (error) {
      console.error("Error fetching users:", error)
    } finally {
      setLoading(false)
    }
  }

  // Reset to first page when search changes
  useEffect(() => {
    setCurrentPage(1)
  }, [searchTerm])

  const handleSuspendUser = async () => {
    if (!selectedUser) return

    try {
      const suspendedUntil = suspensionForm.isPermanent
        ? null
        : suspensionForm.suspendedUntil
          ? new Date(suspensionForm.suspendedUntil).toISOString()
          : null

      const { error } = await supabase.from("user_suspensions").insert({
        user_id: selectedUser.user_id,
        admin_id: (await supabase.auth.getUser()).data.user?.id,
        reason: suspensionForm.reason,
        suspended_until: suspendedUntil,
        is_permanent: suspensionForm.isPermanent,
      })

      if (error) throw error

      await fetchUsers()
      setSuspendDialogOpen(false)
      setSuspensionForm({ reason: "", isPermanent: false, suspendedUntil: "" })
      alert("User suspended successfully!")
    } catch (error: any) {
      console.error("Error suspending user:", error)
      alert(`Error suspending user: ${error.message}`)
    }
  }

  const handleUnsuspendUser = async (userId: string) => {
    if (!confirm("Are you sure you want to unsuspend this user?")) {
      return
    }

    try {
      const { error } = await supabase.from("user_suspensions").delete().eq("user_id", userId)

      if (error) throw error

      await fetchUsers()
      alert("User unsuspended successfully!")
    } catch (error: any) {
      console.error("Error unsuspending user:", error)
      alert(`Error unsuspending user: ${error.message}`)
    }
  }

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-lg">Loading users...</div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Users className="h-5 w-5" />
            User Management
          </CardTitle>
        </CardHeader>
        <CardContent>
          {/* Search */}
          <div className="relative mb-6">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              placeholder="Search users by name or email..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>

          {/* Users List */}
          <div className="space-y-4">
            {users.map((user) => (
              <Card key={user.user_id} className={`${user.is_suspended ? "border-red-200 bg-red-50" : ""}`}>
                <CardContent className="p-4">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <h3 className="font-medium">{user.full_name || "No Name"}</h3>
                        {user.is_suspended && (
                          <Badge className="bg-red-100 text-red-800">
                            <UserX className="h-3 w-3 mr-1" />
                            Suspended
                          </Badge>
                        )}
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-gray-600">
                        <div className="space-y-1">
                          <div className="flex items-center gap-1">
                            <Mail className="h-3 w-3" />
                            <span>{user.email}</span>
                          </div>
                          {user.last_activity && (
                            <div className="flex items-center gap-1">
                              <Calendar className="h-3 w-3" />
                              <span>Last active: {new Date(user.last_activity).toLocaleDateString()}</span>
                            </div>
                          )}
                        </div>

                        <div className="space-y-1">
                          <div className="flex items-center gap-1">
                            <Package className="h-3 w-3" />
                            <span>{user.total_items} total items</span>
                          </div>
                          <div className="flex items-center gap-1">
                            <span className="w-3 h-3 bg-green-500 rounded-full"></span>
                            <span>{user.active_items} active</span>
                          </div>
                          <div className="flex items-center gap-1">
                            <span className="w-3 h-3 bg-blue-500 rounded-full"></span>
                            <span>{user.completed_items} completed</span>
                          </div>
                          {user.reports_count > 0 && (
                            <div className="flex items-center gap-1">
                              <AlertTriangle className="h-3 w-3 text-red-500" />
                              <span className="text-red-600">{user.reports_count} reports</span>
                            </div>
                          )}
                        </div>
                      </div>

                      {user.is_suspended && user.suspension_reason && (
                        <div className="mt-2 p-2 bg-red-100 rounded text-sm">
                          <strong>Suspension reason:</strong> {user.suspension_reason}
                        </div>
                      )}
                    </div>

                    <div className="flex gap-2 ml-4">
                      {user.is_suspended ? (
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleUnsuspendUser(user.user_id)}
                          className="text-green-600 hover:text-green-700"
                        >
                          <UserCheck className="h-3 w-3 mr-1" />
                          Unsuspend
                        </Button>
                      ) : (
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => {
                            setSelectedUser(user)
                            setSuspendDialogOpen(true)
                          }}
                          className="text-red-600 hover:text-red-700"
                        >
                          <UserX className="h-3 w-3 mr-1" />
                          Suspend
                        </Button>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center items-center gap-2 mt-6">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                disabled={currentPage === 1}
              >
                Previous
              </Button>
              <span className="text-sm text-gray-600">
                Page {currentPage} of {totalPages}
              </span>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                disabled={currentPage === totalPages}
              >
                Next
              </Button>
            </div>
          )}

          {users.length === 0 && !loading && (
            <div className="text-center py-12">
              <Users className="h-12 w-12 mx-auto mb-4 text-gray-400" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No users found</h3>
              <p className="text-gray-600">Try adjusting your search terms.</p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Suspend User Dialog */}
      <Dialog open={suspendDialogOpen} onOpenChange={setSuspendDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Suspend User</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <p className="text-sm text-gray-600 mb-4">
                You are about to suspend <strong>{selectedUser?.full_name || selectedUser?.email}</strong>
              </p>
            </div>

            <div>
              <Label htmlFor="reason">Reason for suspension *</Label>
              <Textarea
                id="reason"
                value={suspensionForm.reason}
                onChange={(e) => setSuspensionForm({ ...suspensionForm, reason: e.target.value })}
                placeholder="Explain why this user is being suspended..."
                rows={3}
                required
              />
            </div>

            <div className="flex items-center space-x-2">
              <input
                type="checkbox"
                id="isPermanent"
                checked={suspensionForm.isPermanent}
                onChange={(e) => setSuspensionForm({ ...suspensionForm, isPermanent: e.target.checked })}
              />
              <Label htmlFor="isPermanent">Permanent suspension</Label>
            </div>

            {!suspensionForm.isPermanent && (
              <div>
                <Label htmlFor="suspendedUntil">Suspend until</Label>
                <Input
                  id="suspendedUntil"
                  type="datetime-local"
                  value={suspensionForm.suspendedUntil}
                  onChange={(e) => setSuspensionForm({ ...suspensionForm, suspendedUntil: e.target.value })}
                />
              </div>
            )}

            <div className="flex gap-2">
              <Button variant="outline" onClick={() => setSuspendDialogOpen(false)} className="flex-1">
                Cancel
              </Button>
              <Button
                onClick={handleSuspendUser}
                className="flex-1 bg-red-600 hover:bg-red-700"
                disabled={!suspensionForm.reason.trim()}
              >
                Suspend User
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  )
}

